                            n_cell.charge = Math.max(0, n_cell.charge - 1)

                if cell.element === 0: // Beacon replenishment
                    cell.charge = Math.min(9, cell.charge + 1)

        grid = next_grid

    return grid // Final state of the grid

function initialize_grid(initial_config, width, height):
    grid = Array(height).fill(0).map(row => Array(width).fill(0).map(cell => ({element: 1, charge: 5})))
    if initial_config:
        for y from 0 to len(initial_config)-1:
            for x from 0 to len(initial_config[y])-1:
                char = initial_config[y][x]
                if char == 'D':
