                else if char == 'B':
                    grid[y][x] = {element: 0, charge: 5}
                else if char.length == 2 && isDigit(char[0]) && isDigit(char[1]):
                    grid[y][x] = {element: parseInt(char[0]), charge: parseInt(char[1])}
                else if isDigit(char):
                    grid[y][x] = {element: parseInt(char), charge: 5}
    return grid

function find_active_element(grid, target_element_type): // Returns {y,x} or null
