            if grid[y][x].element === target_element_type:
                candidates.push({y,x,charge: grid[y][x].charge})

    if len(candidates) === 0: return null

    sort(candidates, by 'charge' (descending), then 'y' (ascending), then 'x' (ascending))
    return {y: candidates[0].y, x: candidates[0].x}

