class Simulation {
  constructor() {
    this.grid = [];
    this.width = 0;
    this.height = 0;
  }

  _manhattanDistance(p1, p2) {
    const dx = Math.abs(p1.x - p2.x);
    const dy = Math.abs(p1.y - p2.y);
    return Math.min(dx, this.width - dx) + Math.min(dy, this.height - dy);
  }

  _findActiveElement(grid, targetElementType) {
    let candidates = [];
    for (let y = 0; y < this.height; y++) {
      for (let x = 0; x < this.width; x++) {
        if (grid[y][x].element === targetElementType) {
          candidates.push({ y: y, x: x, charge: grid[y][x].charge });
        }
      }
    }

    if (candidates.length === 0) return null;

    // Sort by charge (desc), then y (asc), then x (asc)
    candidates.sort((a, b) => {
      if (b.charge !== a.charge) return b.charge - a.charge;
      if (a.y !== b.y) return a.y - b.y;
      return a.x - b.x;
    });
    return { y: candidates[0].y, x: candidates[0].x };
  }

  run(parameters) {
    const initialConfig = parameters.initial_config;
    this.width = parameters.width;
    this.height = parameters.height;
    const maxSteps = parameters.max_steps;

    // Initialize grid
    this.grid = Array(this.height).fill(0).map(() =>
      Array(this.width).fill(0).map(() => ({ element: 1, charge: 5 }))
    );

    if (initialConfig) {
      for (let y = 0; y < initialConfig.length; y++) {
        if (initialConfig[y] && initialConfig[y].length > 0) {
          for (let x = 0; x < initialConfig[y].length; x++) {
            const char = initialConfig[y][x];
            if (char === 'D') {
              this.grid[y][x] = { element: 3, charge: 5 };
            } else if (char === 'C') {
              this.grid[y][x] = { element: 4, charge: 5 };
            } else if (char === 'B') {
              this.grid[y][x] = { element: 0, charge: 5 };
            } else if (initialConfig[y][x+1] && !isNaN(parseInt(char)) && !isNaN(parseInt(initialConfig[y][x+1]))) {
              // Check for two-digit element+charge like '05'
              this.grid[y][x] = { element: parseInt(char), charge: parseInt(initialConfig[y][x+1]) };
              x++; // Skip next character as it was consumed
            } else if (!isNaN(parseInt(char))) {
              // Single digit element, default charge
              this.grid[y][x] = { element: parseInt(char), charge: 5 };
            }
          }
        }
      }
    }

    for (let step = 0; step < maxSteps; step++) {
      const prevGrid = JSON.parse(JSON.stringify(this.grid)); // Deep copy
      const nextGrid = JSON.parse(JSON.stringify(prevGrid)); // Deep copy for next state

      let movementOrderCounter = 0;
      const intendedMoves = []; // [{from: {y,x}, to: {ny,nx}, original_element_val: {element, charge}, order: int}]
      const movedElementMapping = {}; // Key: 'ny,nx', Value: {from: {y,x}, to: {ny,nx}, original_element_val: {element, charge}, order: int}

      // 1. Element Movement Phase (Charge-Driven)
      const activeDrain = this._findActiveElement(prevGrid, 3);

      for (let y = 0; y < this.height; y++) {
        for (let x = 0; x < this.width; x++) {
          const cell = prevGrid[y][x];
          if (cell.element !== 3 && cell.element !== -1) { // Not drain and not empty
            if (activeDrain) {
              let preferredTarget = null;
              let minDist = Infinity;

              const neighborsCoords = [
                { dy: -1, dx: 0 }, // Up
                { dy: 1, dx: 0 },  // Down
                { dy: 0, dx: -1 }, // Left
                { dy: 0, dx: 1 }   // Right
              ];

              for (let nIdx = 0; nIdx < neighborsCoords.length; nIdx++) {
                const neighborCoord = neighborsCoords[nIdx];
                const ny = (y + neighborCoord.dy + this.height) % this.height;
                const nx = (x + neighborCoord.dx + this.width) % this.width;

                const dist = this._manhattanDistance({ y: ny, x: nx }, activeDrain);

                if (dist < minDist) {
                  minDist = dist;
                  preferredTarget = { y: ny, x: nx };
                }
                // Tie-breaker is implicit by iteration order (Up, Down, Left, Right)
              }

              if (preferredTarget) {
                const targetCellVal = prevGrid[preferredTarget.y][preferredTarget.x].charge;
                if (cell.charge > targetCellVal) {
                  intendedMoves.push({
                    from: { y: y, x: x },
                    to: preferredTarget,
                    original_element_val: JSON.parse(JSON.stringify(cell)), // Deep copy original element
                    order: movementOrderCounter++
                  });
                }
              }
            }
          }
        }
      }

      // Sort intended moves for resolution
      // Primary: from.y, then from.x
      // Secondary: to.y, then to.x
      intendedMoves.sort((a, b) => {
        if (a.from.y !== b.from.y) return a.from.y - b.from.y;
        if (a.from.x !== b.from.x) return a.from.x - b.from.x;
        if (a.to.y !== b.to.y) return a.to.y - b.to.y;
        return a.to.x - b.to.x;
      });

      const occupiedNextCells = new Set(); // Stores 'y,x' strings of occupied cells

      for (const move of intendedMoves) {
        const targetKey = `${move.to.y},${move.to.x}`;
        if (!occupiedNextCells.has(targetKey)) {
          nextGrid[move.to.y][move.to.x] = move.original_element_val;
          nextGrid[move.from.y][move.from.x] = { element: -1, charge: 0 }; // Empty original cell
          occupiedNextCells.add(targetKey);
          movedElementMapping[targetKey] = move;
        } else {
          // Element fails to move, its original cell in nextGrid remains its original value.
          // Because nextGrid was deep copied from prevGrid initially, no change needed here.
        }
      }

      // 1.e. Conduit Redirection (Post-Movement)
      const activeBeacon = this._findActiveElement(nextGrid, 0);
      const conduitTeleports = []; // [{from: {y,x}, element_data: {element,charge}, original_move_order: int}]

      for (let y = 0; y < this.height; y++) {
        for (let x = 0; x < this.width; x++) {
          const cell = nextGrid[y][x];
          if (cell.element === 4) { // Is a conduit
            const targetKey = `${y},${x}`;
            // Check if an element *just moved* into this conduit cell in this step
            if (movedElementMapping.hasOwnProperty(targetKey)) {
                const moveInfo = movedElementMapping[targetKey];
                conduitTeleports.push({
                    from: {y:moveInfo.to.y, x:moveInfo.to.x}, // The conduit cell itself
                    element_data: moveInfo.original_element_val, // The element that moved into it
                    original_move_order: moveInfo.order
                });
            } else if (prevGrid[y][x].element !== 4 && nextGrid[y][x].element === 4) {
                // This case handles a conduit appearing (not just element moving into it). If it IS a conduit it still functions
                // However, the rule states 'any element that enters it'. So if it was already a conduit and an element was ALREADY there, it doesn't teleport.
                // The current rule implies only elements that *moved into* a conduit will be teleported.
                // If an element was already in a conduit initially, it just sits there.
            }
          }
        }
      }

      // Sort conduit teleports by original_move_order, then by 'from' cell (row-major)
      conduitTeleports.sort((a, b) => {
          if (a.original_move_order !== b.original_move_order) return a.original_move_order - b.original_move_order;
          if (a.from.y !== b.from.y) return a.from.y - b.from.y;
          return a.from.x - b.from.x; 
      });

      if (activeBeacon) {
        const beaconOccupants = {}; // Stores 'y,x' strings to track if beacon is occupied
        for (const teleportInfo of conduitTeleports) {
          const beaconKey = `${activeBeacon.y},${activeBeacon.x}`;
          if (!beaconOccupants.hasOwnProperty(beaconKey)) {
            // First element to be redirected to this beacon takes it
            nextGrid[activeBeacon.y][activeBeacon.x] = teleportInfo.element_data;
            nextGrid[teleportInfo.from.y][teleportInfo.from.x] = { element: -1, charge: 0 }; // Clear conduit cell
            beaconOccupants[beaconKey] = true;
          } else {
            // Subsequent elements redirected to the same beacon are lost
            nextGrid[teleportInfo.from.y][teleportInfo.from.x] = { element: -1, charge: 0 };
          }
        }
      }

      // 2. Charge Update Phase on final `nextGrid`
      for (let y = 0; y < this.height; y++) {
        for (let x = 0; x < this.width; x++) {
          const cell = nextGrid[y][x];

          if (cell.element === 3) { // Drain consumption
            const neighborsCoordsAdj = [
              { dy: -1, dx: 0 }, { dy: 1, dx: 0 }, { dy: 0, dx: -1 }, { dy: 0, dx: 1 }
            ];
            for (const neighborCoord of neighborsCoordsAdj) {
              const ny = (y + neighborCoord.dy + this.height) % this.height;
              const nx = (x + neighborCoord.dx + this.width) % this.width;
              const nCell = nextGrid[ny][nx]; // Get reference
              if (nCell.element !== 3 && nCell.element !== -1) {
                // Directly modify the charge in nextGrid
                nCell.charge = Math.max(0, nCell.charge - 1);
              }
            }
          }

          // Re-get cell reference as it might have changed if drain was also a neighbor
          const currentCell = nextGrid[y][x]; 
          if (currentCell.element === 0) { // Beacon replenishment
            currentCell.charge = Math.min(9, currentCell.charge + 1);
          }
        }
      }

      // 3. Grid Swap
      this.grid = nextGrid;
    }

    return { final_grid: this.grid };
  }
}

