// VALIDATOR IMPLEMENTATION FOR: Elemental Grid Conduit

class Simulation {
  constructor() {
    // purely deterministic  no mutable globals outside run
  }

  // ---------- Helper utilities ----------
  static _isDigit(ch) {
    return ch >= '0' && ch <= '9';
  }

  static _cloneCell(cell) {
    return { element: cell.element, charge: cell.charge };
  }

  static _cloneGrid(grid) {
    return grid.map(row => row.map(Simulation._cloneCell));
  }

  static _rowMajorCompare(a, b) {
    if (a.y !== b.y) return a.y - b.y;
    return a.x - b.x;
  }

  // Toroidal Manhattan distance
  static _toroidalManhattan(p1, p2, width, height) {
    const dx = Math.abs(p1.x - p2.x);
    const dy = Math.abs(p1.y - p2.y);
    return Math.min(dx, width - dx) + Math.min(dy, height - dy);
  }

  // Find active element (drain or beacon) according to rules
  static _findActiveElement(grid, targetElement) {
    const height = grid.length;
    const width = grid[0].length;
    const candidates = [];
    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        if (grid[y][x].element === targetElement) {
          candidates.push({ y, x, charge: grid[y][x].charge });
        }
      }
    }
    if (candidates.length === 0) return null;
    candidates.sort((a, b) => {
      if (a.charge !== b.charge) return b.charge - a.charge; // highest charge first
      const rm = Simulation._rowMajorCompare(a, b);
      return rm;
    });
    return { y: candidates[0].y, x: candidates[0].x };
  }

  // Initialise the grid from parameters according to specification
  static _initialiseGrid(initialConfig, width, height) {
    // default grid
    const grid = Array.from({ length: height }, () =>
      Array.from({ length: width }, () => ({ element: 1, charge: 5 }))
    );
    if (!initialConfig) return grid;

    for (let y = 0; y < Math.min(height, initialConfig.length); y++) {
      const rowStr = initialConfig[y] == null ? '' : String(initialConfig[y]);
      const rowTokens = rowStr.trim().split(/\s+/);
      // Decide whether tokenised or raw string
      const useTokens = rowTokens.length >= width;
      let idx = 0; // pointer for raw string
      for (let x = 0; x < width; x++) {
        let token;
        if (useTokens) {
          token = rowTokens[x] !== undefined ? rowTokens[x] : '';
        } else {
          // raw scanning skipping whitespaces
          while (idx < rowStr.length && /\s/.test(rowStr[idx])) idx++;
          if (idx >= rowStr.length) token = '';
          else {
            const ch = rowStr[idx];
            if (ch === 'D' || ch === 'C' || ch === 'B') {
              token = ch;
              idx += 1;
            } else if (Simulation._isDigit(ch)) {
              if (idx + 1 < rowStr.length && Simulation._isDigit(rowStr[idx + 1])) {
                token = ch + rowStr[idx + 1];
                idx += 2;
              } else {
                token = ch;
                idx += 1;
              }
            } else {
              token = ch;
              idx += 1;
            }
          }
        }

        let element = 1;
        let charge = 5;

        if (token === 'D' || token === 'DRAIN_POS') {
          element = 3;
        } else if (token === 'C') {
          element = 4;
        } else if (token === 'B' || token === 'BEACON_POS') {
          element = 0;
        } else if (token.length === 2 && Simulation._isDigit(token[0]) && Simulation._isDigit(token[1])) {
          element = Number(token[0]);
          charge = Number(token[1]);
        } else if (token.length === 1 && Simulation._isDigit(token[0])) {
          element = Number(token[0]);
        }
        grid[y][x] = { element, charge };
      }
    }
    return grid;
  }

  // ---------- Main run ----------
  run(parameters) {
    // Extract parameters with defaults
    const maxSteps = parameters.max_steps != null ? parameters.max_steps : 1;
    const height =
      parameters.height != null
        ? parameters.height
        : parameters.initial_config
        ? parameters.initial_config.length
        : 0;
    const width =
      parameters.width != null
        ? parameters.width
        : parameters.initial_config && parameters.initial_config.length
        ? parameters.initial_config[0].length
        : 0;
    const initialConfig = parameters.initial_config || [];

    let grid = Simulation._initialiseGrid(initialConfig, width, height);

    // ----- Simulation loop -----
    for (let step = 0; step < maxSteps; step++) {
      const prevGrid = Simulation._cloneGrid(grid);
      const nextGrid = Simulation._cloneGrid(grid);

      // Movement phase
      const activeDrain = Simulation._findActiveElement(prevGrid, 3);

      const intendedMoves = [];
      let moveCounter = 0;

      // Collect intended moves
      for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
          const cell = prevGrid[y][x];
          if (cell.element === -1 || cell.element === 3) continue; // skip empty & drain
          if (!activeDrain) continue; // no movement without drain

          let preferredTarget = null;
          let minDist = Infinity;
          const neighbors = [
            { dy: -1, dx: 0 }, // Up
            { dy: 1, dx: 0 }, // Down
            { dy: 0, dx: -1 }, // Left
            { dy: 0, dx: 1 }, // Right
          ];

          for (const nb of neighbors) {
            const ny = (y + nb.dy + height) % height;
            const nx = (x + nb.dx + width) % width;
            const dist = Simulation._toroidalManhattan(
              { y: ny, x: nx },
              activeDrain,
              width,
              height
            );
            if (dist < minDist) {
              minDist = dist;
              preferredTarget = { y: ny, x: nx };
            }
          }

          if (preferredTarget) {
            const targetCharge = prevGrid[preferredTarget.y][preferredTarget.x].charge;
            if (cell.charge > targetCharge) {
              intendedMoves.push({
                from: { y, x },
                to: preferredTarget,
                original_element_val: { element: cell.element, charge: cell.charge },
                order: moveCounter++,
              });
            }
          }
        }
      }

      // Sort intended moves
      intendedMoves.sort((a, b) => {
        const cmpFrom = Simulation._rowMajorCompare(a.from, b.from);
        if (cmpFrom !== 0) return cmpFrom;
        return Simulation._rowMajorCompare(a.to, b.to);
      });

      const occupiedTargets = new Set();
      const movedElementMapping = Object.create(null); // key = "y,x"

      // Resolve movements
      for (const mv of intendedMoves) {
        const tgtKey = `${mv.to.y},${mv.to.x}`;
        if (occupiedTargets.has(tgtKey)) {
          // movement fails  stays put
          continue;
        }
        // perform move
        nextGrid[mv.to.y][mv.to.x] = Simulation._cloneCell(mv.original_element_val);
        nextGrid[mv.from.y][mv.from.x] = { element: -1, charge: 0 };
        occupiedTargets.add(tgtKey);
        movedElementMapping[tgtKey] = mv;
      }

      // Conduit redirection
      const activeBeacon = Simulation._findActiveElement(nextGrid, 0);

      const conduitTeleports = [];
      for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
          const cell = nextGrid[y][x];
          if (cell.element === 4) {
            const key = `${y},${x}`;
            if (Object.prototype.hasOwnProperty.call(movedElementMapping, key)) {
              const mvInfo = movedElementMapping[key];
              conduitTeleports.push({
                from: { y, x },
                element_data: Simulation._cloneCell(mvInfo.original_element_val),
                original_move_order: mvInfo.order,
              });
            }
          }
        }
      }

      conduitTeleports.sort((a, b) => {
        if (a.original_move_order !== b.original_move_order)
          return a.original_move_order - b.original_move_order;
        return Simulation._rowMajorCompare(a.from, b.from);
      });

      if (activeBeacon) {
        let beaconOccupied = false;
        for (const tp of conduitTeleports) {
          if (!beaconOccupied) {
            nextGrid[activeBeacon.y][activeBeacon.x] = Simulation._cloneCell(
              tp.element_data
            );
            beaconOccupied = true;
          }
          // clear conduit cell
          nextGrid[tp.from.y][tp.from.x] = { element: -1, charge: 0 };
        }
      }
      // if no active beacon, elements remain in conduit cells (no clearing)

      // Charge update phase
      for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
          const cell = nextGrid[y][x];
          // Drain consumption
          if (cell.element === 3) {
            const neighbors = [
              { dy: -1, dx: 0 },
              { dy: 1, dx: 0 },
              { dy: 0, dx: -1 },
              { dy: 0, dx: 1 },
            ];
            for (const nb of neighbors) {
              const ny = (y + nb.dy + height) % height;
              const nx = (x + nb.dx + width) % width;
              const nCell = nextGrid[ny][nx];
              if (nCell.element !== 3 && nCell.element !== -1) {
                nCell.charge = Math.max(0, nCell.charge - 1);
              }
            }
          }
        }
      }

      // Beacon replenishment (after drains to match order)
      for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
          const cell = nextGrid[y][x];
          if (cell.element === 0) {
            cell.charge = Math.min(9, cell.charge + 1);
          }
        }
      }

      // Prepare for next step
      grid = nextGrid;
    }

    // Return final state
    return { final_grid: Simulation._cloneGrid(grid) };
  }
}
