function simulate(initial_grid_config, max_ticks):
  grid = initialize_grid(initial_grid_config) // Deep copy of config to mutable grid
  rows = grid.length
  cols = grid[0].length
  element_masses = {'Fire': 5, 'Water': 4, 'Earth': 6, 'Air': 3, 'Void': 1}
  fusion_rules = [
    {'inputs': ['Fire', 'Water'], 'output': 'Void', 'output_mass': 2},
    {'inputs': ['Earth', 'Air'], 'output': 'Void', 'output_mass': 2}
  ]
  dissipation_rates = {'Void': 1}

  for tick from 1 to max_ticks:
    // 1. Dissipation
    for r from 0 to rows-1:
      for c from 0 to cols-1:
        conduit = grid[r][c]
        new_elements_after_dissipation = []
        for each element E in conduit.elements (original order):
          if E.type in dissipation_rates:
            E.mass = max(0, E.mass - dissipation_rates[E.type])
          if E.mass > 0:
            add E to new_elements_after_dissipation
        conduit.elements = new_elements_after_dissipation

    // 2. Fusion
    for r from 0 to rows-1:
      for c from 0 to cols-1:
        conduit = grid[r][c]
        can_fuse_in_this_conduit = true
        while can_fuse_in_this_conduit:
          can_fuse_in_this_conduit = false
          for i from 0 to conduit.elements.length - 2:
            e1 = conduit.elements[i]
            for j from i + 1 to conduit.elements.length - 1:
              e2 = conduit.elements[j]
              matched_rule = null
              for each rule in fusion_rules:
                if ( (e1.type == rule.inputs[0] and e2.type == rule.inputs[1]) or \
                     (e1.type == rule.inputs[1] and e2.type == rule.inputs[0]) ):
                  matched_rule = rule
                  break // Found first matching rule

              if matched_rule != null:
                // Remove e1 and e2. Order of removal matters for indices.
                // To maintain correct indices for iteration, recreate array or mark for removal.
                // For simplicity, let's re-build the array for fusion:
                new_elements_after_fusion = []
                for k from 0 to conduit.elements.length - 1:
                  if k != i and k != j:
                    add conduit.elements[k] to new_elements_after_fusion
                add {type: matched_rule.output, mass: matched_rule.output_mass} to new_elements_after_fusion
                conduit.elements = new_elements_after_fusion
                can_fuse_in_this_conduit = true
                break // Restart inner loop for this conduit to re-check fusions
            if can_fuse_in_this_conduit: break // Restart outer loop for this conduit

    // 3. Movement
    next_grid = deep_copy(grid) // Preserve current state for movement decisions

    for r from 0 to rows-1:
      for c from 0 to cols-1:
        current_conduit = grid[r][c]

        target_r, target_c = r, c
        if current_conduit.direction == 'N': target_r = r - 1
        else if current_conduit.direction == 'S': target_r = r + 1
        else if current_conduit.direction == 'E': target_c = c + 1
        else if current_conduit.direction == 'W': target_c = c - 1
        else continue // 'None' or invalid direction, no movement

        if target_r < 0 or target_r >= rows or target_c < 0 or target_c >= cols:
          continue // Target out of bounds

        elements_to_move = []
        remaining_elements_in_source = []
        for each element E in current_conduit.elements (original order):
          current_total_mass_in_target = sum_mass(next_grid[target_r][target_c].elements)
          if current_total_mass_in_target + E.mass <= next_grid[target_r][target_c].capacity:
            add E to elements_to_move
            // Note: E is ADDED to next_grid[target_r][target_c].elements later.
            // E is REMOVED from next_grid[r][c].elements in the cleanup pass below.
          else:
            add E to remaining_elements_in_source

        // Apply moves to next_grid
        // Elements that moved are removed from current conduit in next_grid
        next_grid[r][c].elements = remaining_elements_in_source
        // Elements that moved are added to target conduit in next_grid
        for each element E_moved in elements_to_move:
          add E_moved to next_grid[target_r][target_c].elements

    grid = next_grid // Update grid for next tick

  return grid

function sum_mass(elements):
  total = 0
  for each E in elements: total += E.mass
  return total

function deep_copy(obj): // Implementation of deep copy (e.g. JSON.parse(JSON.stringify(obj)) for simple types)
  // This function needs to handle nested arrays and objects correctly.
  // Given the constraints, a simple JSON stringify/parse is sufficient for Element and Conduit structure.
  return JSON.parse(JSON.stringify(obj))

