class Simulation {
  constructor() {
    this.grid = [];
    this.rows = 0;
    this.cols = 0;
    this.element_masses = {'Fire': 5, 'Water': 4, 'Earth': 6, 'Air': 3, 'Void': 1};
    this.fusion_rules = [
      {'inputs': ['Fire', 'Water'], 'output': 'Void', 'output_mass': 2},
      {'inputs': ['Earth', 'Air'], 'output': 'Void', 'output_mass': 2}
    ];
    this.dissipation_rates = {'Void': 1};
    // time_tick is local to run method for stateless class instance
  }

  _deepCopy(obj) {
    return JSON.parse(JSON.stringify(obj));
  }

  _sumMass(elements) {
    let total = 0;
    for (let i = 0; i < elements.length; i++) {
      total += elements[i].mass;
    }
    return total;
  }

  run(parameters) {
    let grid = this._deepCopy(parameters.initial_grid_config);
    const rows = grid.length;
    const cols = grid[0].length;
    const max_ticks = parameters.max_ticks;

    for (let tick = 1; tick <= max_ticks; tick++) {
      // 1. Dissipation
      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const conduit = grid[r][c];
          const newElementsAfterDissipation = [];
          for (let i = 0; i < conduit.elements.length; i++) {
            const element = conduit.elements[i];
            if (this.dissipation_rates[element.type] !== undefined) {
              element.mass = Math.max(0, element.mass - this.dissipation_rates[element.type]);
            }
            if (element.mass > 0) {
              newElementsAfterDissipation.push(element);
            }
          }
          conduit.elements = newElementsAfterDissipation;
        }
      }

      // 2. Fusion
      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const conduit = grid[r][c];
          let canFuseInThisConduit = true;

          while (canFuseInThisConduit) {
            canFuseInThisConduit = false;
            let fusedThisPass = false;

            if (conduit.elements.length < 2) continue;

            for (let i = 0; i < conduit.elements.length - 1; i++) {
              const e1 = conduit.elements[i];
              for (let j = i + 1; j < conduit.elements.length; j++) {
                const e2 = conduit.elements[j];
                let matchedRule = null;

                for (let k = 0; k < this.fusion_rules.length; k++) {
                  const rule = this.fusion_rules[k];
                  if (
                    (e1.type === rule.inputs[0] && e2.type === rule.inputs[1]) ||
                    (e1.type === rule.inputs[1] && e2.type === rule.inputs[0])
                  ) {
                    matchedRule = rule;
                    break; // Found first matching rule
                  }
                }

                if (matchedRule !== null) {
                  const newElementsAfterFusion = [];
                  for (let k = 0; k < conduit.elements.length; k++) {
                    if (k !== i && k !== j) {
                      newElementsAfterFusion.push(conduit.elements[k]);
                    }
                  }
                  newElementsAfterFusion.push({'type': matchedRule.output, 'mass': matchedRule.output_mass});
                  conduit.elements = newElementsAfterFusion;
                  canFuseInThisConduit = true;
                  fusedThisPass = true;
                  break; // Restart inner loop for this conduit to re-check fusions
                }
              }
              if (fusedThisPass) break; // If fused, break outer loop to restart while
            }
          }
        }
      }

      // 3. Movement
      let nextGrid = this._deepCopy(grid);

      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const currentConduit = grid[r][c]; // Use original grid for deciding elements to move

          let targetR = r;
          let targetC = c;
          let hasTarget = true;

          switch (currentConduit.direction) {
            case 'N': targetR = r - 1; break;
            case 'S': targetR = r + 1; break;
            case 'E': targetC = c + 1; break;
            case 'W': targetC = c - 1; break;
            default: hasTarget = false; break; // 'None' or invalid
          }

          if (!hasTarget || targetR < 0 || targetR >= rows || targetC < 0 || targetC >= cols) {
            // No valid target, elements remain in source in nextGrid
            continue; 
          }

          const elementsToMove = [];
          const remainingElementsInSource = [];

          for (let i = 0; i < currentConduit.elements.length; i++) {
            const element = currentConduit.elements[i];
            const currentTotalMassInTarget = this._sumMass(nextGrid[targetR][targetC].elements);
            if (currentTotalMassInTarget + element.mass <= nextGrid[targetR][targetC].capacity) {
              elementsToMove.push(element);
            } else {
              remainingElementsInSource.push(element);
            }
          }

          // Apply moves to nextGrid
          nextGrid[r][c].elements = remainingElementsInSource;
          for (let i = 0; i < elementsToMove.length; i++) {
            nextGrid[targetR][targetC].elements.push(elementsToMove[i]);
          }
        }
      }
      grid = nextGrid; // Update grid for next tick
    }

    return { grid: grid };
  }
}

