// VALIDATOR IMPLEMENTATION FOR: Elemental Conduit Flow

class Simulation {
  constructor() {
    // No global mutable state outside run
  }

  // ---------- Helper methods ----------
  _deepCopy(obj) {
    // Safe deep copy for plain JSON-compatible objects
    return JSON.parse(JSON.stringify(obj));
  }

  _sumMass(elements) {
    let total = 0;
    for (let i = 0; i < elements.length; i++) {
      total += elements[i].mass;
    }
    return total;
  }

  // ---------- Main run ----------
  run(parameters) {
    // Extract parameters
    const initial_grid_config = parameters.initial_grid_config || [];
    const max_ticks = parameters.max_ticks || 0;

    // 1. Initialise grid (deep copy to avoid mutating input)
    let grid = this._deepCopy(initial_grid_config);
    const rows = grid.length;
    const cols = rows > 0 ? grid[0].length : 0;

    // 2. Static tables
    const element_masses = {
      'Fire': 5,
      'Water': 4,
      'Earth': 6,
      'Air': 3,
      'Void': 1
    };

    const fusion_rules = [
      { inputs: ['Fire', 'Water'], output: 'Void', output_mass: 2 },
      { inputs: ['Earth', 'Air'], output: 'Void', output_mass: 2 }
    ];

    const dissipation_rates = { 'Void': 1 };

    // 3. Simulation loop
    for (let tick = 1; tick <= max_ticks; tick++) {
      // ----- Phase 1: Dissipation -----
      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const conduit = grid[r][c];
          const newElements = [];
          const elems = conduit.elements;
          for (let i = 0; i < elems.length; i++) {
            const e = elems[i];
            if (dissipation_rates.hasOwnProperty(e.type)) {
              e.mass = Math.max(0, e.mass - dissipation_rates[e.type]);
            }
            if (e.mass > 0) {
              newElements.push(e);
            }
          }
          conduit.elements = newElements;
        }
      }

      // ----- Phase 2: Fusion -----
      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const conduit = grid[r][c];
          let canFuse = true;
          while (canFuse) {
            canFuse = false;
            outer:
            for (let i = 0; i < conduit.elements.length - 1; i++) {
              for (let j = i + 1; j < conduit.elements.length; j++) {
                const e1 = conduit.elements[i];
                const e2 = conduit.elements[j];
                for (let fr = 0; fr < fusion_rules.length; fr++) {
                  const rule = fusion_rules[fr];
                  const a = rule.inputs[0];
                  const b = rule.inputs[1];
                  if (
                    (e1.type === a && e2.type === b) ||
                    (e1.type === b && e2.type === a)
                  ) {
                    // Build new elements array excluding indices i and j
                    const rebuilt = [];
                    for (let k = 0; k < conduit.elements.length; k++) {
                      if (k !== i && k !== j) {
                        rebuilt.push(conduit.elements[k]);
                      }
                    }
                    // Append fused element
                    rebuilt.push({ type: rule.output, mass: rule.output_mass });
                    conduit.elements = rebuilt;
                    canFuse = true;
                    break outer;
                  }
                }
              }
            }
          }
        }
      }

      // ----- Phase 3: Movement -----
      let next_grid = this._deepCopy(grid);

      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const current_conduit = grid[r][c];
          let target_r = r;
          let target_c = c;

          switch (current_conduit.direction) {
            case 'N':
              target_r = r - 1;
              break;
            case 'S':
              target_r = r + 1;
              break;
            case 'E':
              target_c = c + 1;
              break;
            case 'W':
              target_c = c - 1;
              break;
            default:
              target_r = -1; // Invalid to signal no movement
          }

          // Check bounds
          if (
            target_r < 0 ||
            target_r >= rows ||
            target_c < 0 ||
            target_c >= cols
          ) {
            continue; // No valid target
          }

          const target_conduit = next_grid[target_r][target_c];
          const remaining_elements = [];
          const elements_to_move = [];

          for (let i = 0; i < current_conduit.elements.length; i++) {
            const elem = current_conduit.elements[i];
            const current_mass_in_target = this._sumMass(
              target_conduit.elements
            );
            if (current_mass_in_target + elem.mass <= target_conduit.capacity) {
              // Move (use deep copy of element)
              elements_to_move.push(this._deepCopy(elem));
            } else {
              remaining_elements.push(this._deepCopy(elem));
            }
          }

          // Update source conduit in next_grid
          next_grid[r][c].elements = remaining_elements;

          // Append moved elements to target conduit in next_grid
          for (let i = 0; i < elements_to_move.length; i++) {
            target_conduit.elements.push(elements_to_move[i]);
          }
        }
      }

      // Update grid for next tick
      grid = next_grid;
    }

    // Return final grid state
    return { grid };
  }
}
