class Simulation {
  constructor() {}

  run(parameters) {
    const size = parameters.size;
    const modulus = parameters.modulus;
    const cycle = parameters.cycle;
    const initial = parameters.initial;
    const steps = parameters.steps;

    // Validation
    if (initial.length !== size) {
      throw new Error('Initial string length must equal size');
    }
    if (size < 3) {
      throw new Error('Size must be at least 3');
    }
    if (modulus < 2) {
      throw new Error('Modulus must be at least 2');
    }
    if (cycle < 1) {
      throw new Error('Cycle must be at least 1');
    }

    // Initialize cells
    let cells = [];
    for (let i = 0; i < size; i++) {
      const digit = parseInt(initial[i]);
      if (isNaN(digit) || digit >= modulus) {
        throw new Error('Invalid digit in initial string');
      }
      cells[i] = digit;
    }

    // Simulate
    for (let step = 0; step < steps; step++) {
      const shift = (step % cycle) + 1;
      const nextCells = new Array(size);

      for (let i = 0; i < size; i++) {
        const left = cells[(i - shift + size) % size];
        const center = cells[i];
        const right = cells[(i + shift) % size];
        nextCells[i] = (left + center + right) % modulus;
      }

      cells = nextCells;
    }

    // Compute sum
    let sum = 0;
    for (let i = 0; i < size; i++) {
      sum += cells[i];
    }

    return {
      cells: cells,
      sum: sum
    };
  }
}

