// VALIDATOR IMPLEMENTATION FOR: Phase-Lock Cellular Ring

class Simulation {
  constructor() {
    // no persistent state needed outside run
  }

  // Helper to throw error with message
  _err(msg) {
    throw new Error(msg);
  }

  // Main runner per specification
  run(parameters) {
    // ----- Parameter extraction -----
    const size    = parameters.size;
    const modulus = parameters.modulus;
    const cycle   = parameters.cycle;
    const initial = parameters.initial;
    const steps   = parameters.steps;

    // ----- Validation (edge cases) -----
    if (!Number.isInteger(size)    || size   < 3) this._err('Invalid size');
    if (!Number.isInteger(modulus) || modulus < 2) this._err('Invalid modulus');
    if (!Number.isInteger(cycle)   || cycle  < 1) this._err('Invalid cycle');
    if (!Number.isInteger(steps)   || steps  < 0) this._err('Invalid steps');

    if (typeof initial !== 'string') this._err('Initial must be string');
    if (initial.length !== size) this._err('Initial length mismatch');

    // Parse initial string into cells array
    const cells = new Array(size);
    for (let i = 0; i < size; i++) {
      const ch = initial.charCodeAt(i);
      if (ch < 48 || ch > 57) this._err('Initial contains non-digit');
      const val = ch - 48; // convert '0'-'9' to 0-9
      if (val >= modulus) this._err('Digit exceeds modulus');
      cells[i] = val;
    }

    // ----- Simulation -----
    let step_count = 0;
    const next = new Array(size);

    for (let step = 0; step < steps; step++) {
      const shift = (step_count % cycle) + 1;

      for (let i = 0; i < size; i++) {
        const leftIdx   = (i - shift + size) % size;
        const rightIdx  = (i + shift) % size;
        const value = (cells[leftIdx] + cells[i] + cells[rightIdx]) % modulus;
        next[i] = value;
      }

      // copy next to cells
      for (let i = 0; i < size; i++) cells[i] = next[i];

      step_count++;
    }

    // ----- Final sum -----
    let total = 0;
    for (let i = 0; i < size; i++) total += cells[i];

    // ----- Return -----
    return {
      cells: cells.slice(), // return a copy
      sum: total
    };
  }
}
