// VALIDATOR IMPLEMENTATION FOR: Resonance Cascade

class Simulation {
  constructor() {
    // No global mutable state; everything lives inside run()
  }

  // Helper to build initial resonators array
  _createInitialResonators(num, charges, freqs) {
    const arr = new Array(num);
    for (let i = 0; i < num; i++) {
      arr[i] = { charge: charges[i], frequency: freqs[i] };
    }
    return arr;
  }

  run(parameters) {
    const N = parameters.num_resonators;

    // Initialize state
    const state = {
      resonators: this._createInitialResonators(
        N,
        parameters.initial_charges,
        parameters.initial_frequencies
      ),
      step: 0,
      total_discharges: 0,
      total_implosions: 0
    };

    // Early exit if no steps requested
    const maxSteps = parameters.max_steps;
    if (maxSteps === 0) {
      return {
        step: state.step,
        total_discharges: state.total_discharges,
        total_implosions: state.total_implosions,
        resonators: state.resonators.map(r => ({
          charge: r.charge,
          frequency: r.frequency
        }))
      };
    }

    // Main simulation loop
    for (let currentStep = 1; currentStep <= maxSteps; currentStep++) {
      // Phase 1: Calculate potential charges
      const potentialCharges = new Array(N);
      for (let i = 0; i < N; i++) {
        potentialCharges[i] =
          state.resonators[i].charge + state.resonators[i].frequency;
      }

      // Phase 2: Identify events
      const dischargingIndices = [];
      const implodingIndices = [];
      for (let i = 0; i < N; i++) {
        const value = potentialCharges[i];
        if (value >= parameters.threshold) {
          dischargingIndices.push(i);
        }
        if (value <= parameters.implosion_threshold) {
          implodingIndices.push(i);
        }
      }

      // Phase 3: Calculate cascade deltas
      const deltaCharges = new Array(N);
      for (let i = 0; i < N; i++) deltaCharges[i] = 0;

      // Discharges
      for (let k = 0; k < dischargingIndices.length; k++) {
        const idx = dischargingIndices[k];
        const cascadeAmount = Math.floor(
          state.resonators[idx].frequency * parameters.attenuation
        );
        if (idx > 0) deltaCharges[idx - 1] += cascadeAmount;
        if (idx < N - 1) deltaCharges[idx + 1] += cascadeAmount;
      }

      // Implosions
      for (let k = 0; k < implodingIndices.length; k++) {
        const idx = implodingIndices[k];
        const cascadeAmount = Math.floor(
          Math.abs(state.resonators[idx].frequency) * parameters.attenuation
        );
        if (idx > 0) deltaCharges[idx - 1] += cascadeAmount;
        if (idx < N - 1) deltaCharges[idx + 1] += cascadeAmount;
      }

      // Phase 4: Apply updates
      const nextCharges = new Array(N);
      for (let i = 0; i < N; i++) {
        nextCharges[i] = potentialCharges[i] + deltaCharges[i];
      }

      // Apply resets for discharges
      for (let k = 0; k < dischargingIndices.length; k++) {
        const idx = dischargingIndices[k];
        state.resonators[idx].frequency *= -1;
        nextCharges[idx] = 0;
        state.total_discharges += 1;
      }

      // Apply resets for implosions
      for (let k = 0; k < implodingIndices.length; k++) {
        const idx = implodingIndices[k];
        state.resonators[idx].frequency *= -1;
        nextCharges[idx] = 0;
        state.total_implosions += 1;
      }

      // Commit final charges
      for (let i = 0; i < N; i++) {
        state.resonators[i].charge = nextCharges[i];
      }
    }

    // Finalize step count
    state.step = maxSteps;

    // Prepare return object conforming to schema
    return {
      step: state.step,
      total_discharges: state.total_discharges,
      total_implosions: state.total_implosions,
      resonators: state.resonators.map(r => ({
        charge: r.charge,
        frequency: r.frequency
      }))
    };
  }
}
