function simulate(seed_pattern, rule_selector, max_steps):
    N = length(seed_pattern)
    cells = array of size N
    for i from 0 to N-1:
        cells[i] = hex_to_int(seed_pattern[i])

    current_step = 0
    while current_step < max_steps:
        next_cells = array of size N
        current_cells_snapshot = copy(cells) // All updates are based on the state at the beginning of the step

        for i from 0 to N-1:
            current_value = current_cells_snapshot[i]
            primary_neighbor_value = current_cells_snapshot[(i + 1) % N]

            if rule_selector == 'A':
                next_cells[i] = (current_value + primary_neighbor_value) % 256
            else if rule_selector == 'B':
                next_cells[i] = (current_value * primary_neighbor_value) % 256
            else if rule_selector == 'C':
                next_cells[i] = (current_value ^ primary_neighbor_value) % 256
            else if rule_selector == 'D':
                next_cells[i] = (current_value - primary_neighbor_value + 256) % 256
            else if rule_selector == 'E':
                next_cells[i] = (((current_value << 1) | (current_value >> 7)) ^ primary_neighbor_value) % 256
            else:
                // Invalid rule_selector, effectively stops evolution
                return cells // Returns the state BEFORE the invalid rule was applied

        cells = next_cells
        current_step = current_step + 1
    return cells

function hex_to_int(char):
    if char is digit '0'-'9': return int(char)
    if char is 'A': return 10
    if char is 'B': return 11
    if char is 'C': return 12
    if char is 'D': return 13
    if char is 'E': return 14
    if char is 'F': return 15
    return 0 // Should not happen with valid input

