class Simulation {
  constructor() {
    this.cells = [];
    this.current_step = 0;
  }

  _hex_to_int(char) {
    const code = char.charCodeAt(0);
    if (code >= 48 && code <= 57) {
      return code - 48; // '0' through '9'
    } else if (code >= 65 && code <= 70) {
      return code - 65 + 10; // 'A' through 'F'
    }
    return 0; // Should not happen with valid input (as per spec, assume valid hex)
  }

  run(parameters) {
    const seedPattern = parameters.seed_pattern;
    const ruleSelector = parameters.rule_selector;
    const maxSteps = parameters.max_steps;

    if (!seedPattern || seedPattern.length === 0) {
      return { cells: [] };
    }

    const N = seedPattern.length;
    this.cells = new Array(N);
    for (let i = 0; i < N; i++) {
      this.cells[i] = { value: this._hex_to_int(seedPattern[i]) };
    }

    this.current_step = 0;
    if (maxSteps === 0) {
      // Deep copy to ensure immutable return for 0 steps
      return { cells: this.cells.map(c => ({ value: c.value })) };
    }

    while (this.current_step < maxSteps) {
      const nextCells = new Array(N);
      const currentCellsSnapshot = this.cells.map(c => ({ value: c.value })); // Snapshot for simultaneous update

      let ruleApplied = true;
      for (let i = 0; i < N; i++) {
        const currentValue = currentCellsSnapshot[i].value;
        const primaryNeighborValue = currentCellsSnapshot[(i + 1) % N].value;
        let newValue;

        switch (ruleSelector) {
          case 'A':
            newValue = (currentValue + primaryNeighborValue) % 256;
            break;
          case 'B':
            newValue = (currentValue * primaryNeighborValue) % 256;
            break;
          case 'C':
            newValue = (currentValue ^ primaryNeighborValue);
            break;
          case 'D':
            newValue = (currentValue - primaryNeighborValue + 256) % 256;
            break;
          case 'E':
            // Left shift by 1, wrapping the MSB to LSB
            // Then XOR with primary neighbor
            newValue = (((currentValue << 1) & 0xFF) | (currentValue >> 7)) ^ primaryNeighborValue;
            // Ensure newValue is within [0, 255] after XOR if it wasn't already
            newValue = newValue % 256;
            break;
          default:
            ruleApplied = false;
            break;
        }
        if (!ruleApplied) {
          // Return deep copy of current state if rule is invalid
          return { cells: this.cells.map(c => ({ value: c.value })) };
        }
        nextCells[i] = { value: newValue };
      }

      this.cells = nextCells;
      this.current_step++;
    }

    return { cells: this.cells.map(c => ({ value: c.value })) }; // Ensure returned object is a deep copy
  }
