// VALIDATOR IMPLEMENTATION FOR: Chaos Cycle Automaton

class Simulation {
  constructor() {
    // No mutable global state needed
  }

  // Helper to convert a single hex character (0-9, A-F) to integer 0-15
  _hexToInt(char) {
    const code = char.charCodeAt(0);
    // '0''9'
    if (code >= 48 && code <= 57) return code - 48;
    // 'A''F'
    if (code >= 65 && code <= 70) return code - 55; // 'A' = 65 -> 10
    // 'a''f' (tolerated, though spec doesn't require)
    if (code >= 97 && code <= 102) return code - 87; // 'a' = 97 -> 10
    return 0; // Undefined behaviour per spec; fallback to 0
  }

  // Compute next value according to selected rule
  _applyRule(rule, current, neighbor) {
    switch (rule) {
      case 'A': // Addition
        return (current + neighbor) & 0xFF;
      case 'B': // Multiplication
        return (current * neighbor) % 256;
      case 'C': // XOR
        return (current ^ neighbor) & 0xFF;
      case 'D': // Subtraction with wrap
        return (current - neighbor + 256) & 0xFF;
      case 'E': { // Rotate-left 1 then XOR
        const rotated =
          ((current << 1) & 0xFF) | ((current >> 7) & 0x01); // 8-bit rotate
        return (rotated ^ neighbor) & 0xFF;
      }
      default:
        return null; // Signal invalid rule
    }
  }

  run(parameters) {
    // Extract parameters with defaults
    const seed_pattern = String(parameters?.seed_pattern ?? '');
    const rule_selector = String(parameters?.rule_selector ?? '');
    const max_steps =
      typeof parameters?.max_steps === 'number' ? parameters.max_steps : 0;

    const N = seed_pattern.length;
    const cells = new Array(N);

    // Initialize cells from seed_pattern
    for (let i = 0; i < N; i++) {
      cells[i] = this._hexToInt(seed_pattern[i]);
    }

    // Edge case: max_steps === 0  return initial state immediately
    if (max_steps === 0) {
      return { cells: cells.map((v) => ({ value: v })) };
    }

    // Validate rule_selector; if invalid, halt immediately and
    // return state before any evolution (spec behaviour)
    if (this._applyRule(rule_selector, 0, 0) === null) {
      return { cells: cells.map((v) => ({ value: v })) };
    }

    let current_step = 0;
    // Main simulation loop
    while (current_step < max_steps) {
      const next_cells = new Array(N);
      const snapshot = cells.slice(); // State at the beginning of the step

      for (let i = 0; i < N; i++) {
        const current_value = snapshot[i];
        const neighbor_value = snapshot[(i + 1) % N];
        const new_val = this._applyRule(
          rule_selector,
          current_value,
          neighbor_value
        );

        // If rule became invalid mid-simulation (should not happen), halt
        if (new_val === null) {
          return { cells: cells.map((v) => ({ value: v })) };
        }
        next_cells[i] = new_val;
      }

      // Commit new values
      for (let i = 0; i < N; i++) cells[i] = next_cells[i];
      current_step++;
    }

    // Wrap numeric values into objects as per return schema
    return { cells: cells.map((v) => ({ value: v })) };
  }
}
