        total_activations += 1
        activated_in_step = true
        pulse = floor(potential_lamps[i] / pulse_factor)
        if i > 0: pulse_deltas[i-1] += pulse
        if i < lamps.length-1: pulse_deltas[i+1] += pulse
        next_lamps[i] = 0
      else:
        next_lamps[i] = potential_lamps[i]

    // Phase 3: Pulse Application
    for i in 0..lamps.length-1:
      next_lamps[i] += pulse_deltas[i]

    // Update state and track max energy
