class Simulation {
  constructor() {
    this.lamps = [];
    this.activation_threshold = 0;
    this.pulse_factor = 1;
    this.max_steps = 0;
    this.total_activations = 0;
    this.max_energy_seen = { value: -1, step: -1, lamp_index: -1 };
  }

  _init(parameters) {
    this.lamps = [...(parameters.initial_state || [])];
    this.activation_threshold = parameters.activation_threshold;
    this.pulse_factor = parameters.pulse_factor >= 1 ? parameters.pulse_factor : 1;
    this.max_steps = parameters.max_steps;

    this.total_activations = 0;
    this.max_energy_seen = { value: -1, step: 0, lamp_index: -1 };

    for (let i = 0; i < this.lamps.length; i++) {
        if (this.lamps[i] > this.max_energy_seen.value) {
            this.max_energy_seen = { value: this.lamps[i], step: 0, lamp_index: i };
        }
    }
  }

  _step() {
    const len = this.lamps.length;
    if (len === 0) return;

    const energy_deltas = new Array(len).fill(0);
    const pulse_deltas = new Array(len).fill(0);
    const next_lamps = new Array(len);

    // Phase 1: Growth
    for (let i = 0; i < len; i++) {
      const left_energy = (i > 0) ? this.lamps[i-1] : 0;
      const right_energy = (i < len - 1) ? this.lamps[i+1] : 0;
      const growth = 1 + (this.lamps[i] % 4) + (left_energy % 4) + (right_energy % 4);
      energy_deltas[i] = growth;
    }

    // Phase 2: Activation
    for (let i = 0; i < len; i++) {
      const potential_energy = this.lamps[i] + energy_deltas[i];
      if (potential_energy >= this.activation_threshold) {
        this.total_activations++;
        const pulse_value = Math.floor(potential_energy / this.pulse_factor);
        if (i > 0) {
          pulse_deltas[i-1] += pulse_value;
        }
        if (i < len - 1) {
          pulse_deltas[i+1] += pulse_value;
        }
        next_lamps[i] = 0;
      } else {
        next_lamps[i] = potential_energy;
      }
    }

    // Phase 3: Pulse Application
    for (let i = 0; i < len; i++) {
      next_lamps[i] += pulse_deltas[i];
    }

    this.lamps = next_lamps;
  }

  run(parameters) {
    this._init(parameters);

    for (let step = 1; step <= this.max_steps; step++) {
      this._step();
      for (let i = 0; i < this.lamps.length; i++) {
          if (this.lamps[i] > this.max_energy_seen.value) {
              this.max_energy_seen = { value: this.lamps[i], step, lamp_index: i };
          }
      }
    }

    return {
      final_step: this.max_steps,
      final_state: this.lamps,
      total_activations: this.total_activations,
      max_energy_seen: this.max_energy_seen
    };
  }
}

