    for (let i = 0; i < lamps.length; i++) {
      if (lamps[i] > max_energy_seen.value) {
        max_energy_seen = { value: lamps[i], step: 0, lamp_index: i };
      }
    }

    // Early termination if no steps requested
    if (max_steps <= 0) {
      return {
        final_step: max_steps,
        final_state: lamps,
        total_activations: total_activations,
        max_energy_seen: max_energy_seen
      };
    }

    // Main simulation loop
