      const len = lamps.length;
      const energy_deltas = new Array(len).fill(0);

      // Phase 1: Growth
      for (let i = 0; i < len; i++) {
        const left  = (i > 0)        ? lamps[i - 1] : 0;
        const right = (i < len - 1)  ? lamps[i + 1] : 0;

        const growth = 1 + (lamps[i] % 4) + (left % 4) + (right % 4);
