function simulate(seed_grid, total_steps, max_charge, fusions_map_string):
  rows = seed_grid.length
  cols = seed_grid[0].length
  grid = initialize_grid(seed_grid)
  fusions_map = parse_fusions_map(fusions_map_string)

  for step = 1 to total_steps:
    next_grid = deep_copy(grid)

    for r = 0 to rows-1:
      for c = 0 to cols-1:
        current_orb_type = grid[r][c].type
        current_orb_charge = grid[r][c].charge
        new_type = current_orb_type
        new_charge = Math.max(0, current_orb_charge - 1)
        fusion_found = false

        neighbors = [
          { r: r-1, c: c }, 
          { r: r+1, c: c }, 
          { r: r, c: c-1 }, 
          { r: r, c: c+1 }
        ]

        for each neighbor_pos in neighbors:
          nr = neighbor_pos.r
          nc = neighbor_pos.c
          neighbor_type = get_neighbor_type(grid, nr, nc, rows, cols)

          fusion_key = current_orb_type + ',' + neighbor_type

          if fusions_map.has(fusion_key):
            new_type = fusions_map.get(fusion_key)
            new_charge = Math.min(max_charge, current_orb_charge + 1)
            fusion_found = true
            break // Only first matching fusion wins

        next_grid[r][c].type = new_type
        next_grid[r][c].charge = new_charge

    grid = next_grid

  return grid.map(row => row.map(cell => ({ type: cell.type, charge: cell.charge }))) // Return final state

function initialize_grid(seed_grid):
  rows = seed_grid.length
  cols = seed_grid[0].length
  grid = new Array(rows).fill(0).map(() => new Array(cols).fill(0))
  for r = 0 to rows-1:
    for c = 0 to cols-1:
      grid[r][c] = { type: seed_grid[r][c], charge: 0 }
  return grid

function parse_fusions_map(fusions_map_string):
  map = new Map()
  parts = fusions_map_string.split(';')
  for part in parts:
    if part.trim() == '' continue
    rule_parts = part.split('>')
    if rule_parts.length == 2:
      map.set(rule_parts[0].trim(), rule_parts[1].trim())
  return map

function get_neighbor_type(grid, r, c, rows, cols):
  if r < 0 or r >= rows or c < 0 or c >= cols:
    return 'V' // Void for out-of-bounds
  return grid[r][c].type

