class Simulation {
  constructor() {}

  run(parameters) {
    const seedGrid = parameters.seed_grid;
    const totalSteps = parameters.total_steps;
    const maxCharge = parameters.max_charge;
    const fusionsMapString = parameters.fusions_map;

    const rows = seedGrid.length;
    const cols = seedGrid[0].length;

    let grid = new Array(rows).fill(0).map((_, r) =>
      new Array(cols).fill(0).map((_, c) => ({ type: seedGrid[r][c], charge: 0 }))
    );

