    fusionsMapString.split(';').forEach(part => {
      if (part.trim() !== '') {
        const ruleParts = part.split('>');
        if (ruleParts.length === 2 && ruleParts[0].trim() !== '' && ruleParts[1].trim() !== '') {
          fusionsMap.set(ruleParts[0].trim(), ruleParts[1].trim());
        }
      }
    });

    const getNeighborType = (currentGrid, r, c, maxR, maxC) => {
      if (r < 0 || r >= maxR || c < 0 || c >= maxC) {
        return 'V'; // Void for out-of-bounds
      }
      return currentGrid[r][c].type;
    };

    for (let step = 0; step < totalSteps; step++) {
      const nextGrid = new Array(rows).fill(0).map((_, r) =>
        new Array(cols).fill(0).map((_, c) => ({
          type: grid[r][c].type,
          charge: grid[r][c].charge
        }))
      ); // Deep copy current grid

      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const currentOrb = grid[r][c];
