class Simulation {
  constructor() {
    // No external mutable state
  }

  // Helper to parse the fusions map string into an object
  _parseFusionsMap(mapStr) {
    const map = Object.create(null);
    if (!mapStr) return map;
    const rules = mapStr.split(';');
    for (let rule of rules) {
      rule = rule.trim();
      if (!rule) continue;
      const parts = rule.split('>');
      if (parts.length !== 2) continue;
      const key = parts[0].trim();
      const value = parts[1].trim();
      if (key && value) {
        map[key] = value;
      }
    }
    return map;
  }

  // Helper to obtain neighbor type with out-of-bounds treated as 'V'
  _neighborType(grid, r, c, rows, cols) {
    if (r < 0 || r >= rows || c < 0 || c >= cols) return 'V';
    return grid[r][c].type;
  }

  run(parameters) {
    const seedGrid   = parameters.seed_grid;
    const totalSteps = parameters.total_steps;
    const maxCharge  = parameters.max_charge;
    const mapString  = parameters.fusions_map || '';

    const rows = seedGrid.length;
    const cols = seedGrid[0] ? seedGrid[0].length : 0;

    // Initialize grid with type and charge 0
    const grid = new Array(rows);
    for (let r = 0; r < rows; r++) {
      grid[r] = new Array(cols);
      const rowStr = seedGrid[r];
      for (let c = 0; c < cols; c++) {
        grid[r][c] = { type: rowStr[c], charge: 0 };
      }
    }

    const fusionsMap = this._parseFusionsMap(mapString);

    for (let step = 0; step < totalSteps; step++) {
      // Prepare next grid
      const nextGrid = new Array(rows);
      for (let r = 0; r < rows; r++) {
        nextGrid[r] = new Array(cols);
      }

      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const currentType   = grid[r][c].type;
          const currentCharge = grid[r][c].charge;

          let newType   = currentType;
          let newCharge = currentCharge > 0 ? currentCharge - 1 : 0;

          // Neighbor scan order: up, down, left, right
          const neighbors = [
            [r - 1, c],
            [r + 1, c],
            [r, c - 1],
            [r, c + 1]
          ];

          for (let i = 0; i < neighbors.length; i++) {
            const [nr, nc] = neighbors[i];
            const neighborType = this._neighborType(grid, nr, nc, rows, cols);
            const key = currentType + ',' + neighborType;
            if (Object.prototype.hasOwnProperty.call(fusionsMap, key)) {
              newType   = fusionsMap[key];
              newCharge = currentCharge + 1;
              if (newCharge > maxCharge) newCharge = maxCharge;
              break; // First matching fusion wins
            }
          }

          nextGrid[r][c] = { type: newType, charge: newCharge };
        }
      }

      // Apply next grid simultaneously
      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          grid[r][c] = nextGrid[r][c];
        }
      }
    }

    // Prepare and return final state
    const result = new Array(rows);
    for (let r = 0; r < rows; r++) {
      result[r] = new Array(cols);
      for (let c = 0; c < cols; c++) {
        result[r][c] = { type: grid[r][c].type, charge: grid[r][c].charge };
      }
    }
    return result;
  }
