class Simulation {
  constructor() {}
  run(parameters) {
    const ringStr = parameters.ring;
    const steps   = parameters.steps >>> 0; // to uint32, fully deterministic
    const N = ringStr.length;
    // current and next buffers
    const current = new Array(N);
    const next    = new Array(N);
    for (let i = 0; i < N; i++) current[i] = ringStr.charCodeAt(i) - 48; // ASCII '0'48
    let step = 0;
    if (steps > 0) {
      while (step < steps) {
        const even = (step & 1) === 0;
        for (let i = 0; i < N; i++) {
          const left  = current[(i + N - 1) % N];
          const right = current[(i + 1) % N];
          if (even) {
            next[i] = (left + current[i] + right) % 10;
          } else {
            next[i] = (left * right + current[i]) % 10;
          }
        }
        // swap references in O(1)
        for (let i = 0; i < N; i++) {
          const tmp   = current[i];
          current[i] = next[i];
          next[i]    = tmp; // reuse array without re-alloc
        }
        step++;
      }
    }
    // Build return snapshot
    const digitCounts = new Array(10).fill(0);
    let sum = 0;
    let finalRing = "";
    for (let i = 0; i < N; i++) {
      const d = current[i];
      digitCounts[d]++;
      sum += d;
      finalRing += d;
    }
    return {
      finalRing: finalRing,
      sumDigits: sum,
      digitCounts: digitCounts,
      stepsExecuted: steps
    };
  }
}

