    return ch.charCodeAt(0) - 48; // '0'.charCodeAt(0) === 48
  }

  run(parameters) {
    // Extract and validate input parameters
    const ringString = String(parameters.ring || "");
    const steps      = Number(parameters.steps || 0);

    const N = ringString.length;
    const current = new Array(N);
    const next    = new Array(N);

    // Initialise current configuration from ringString
    for (let i = 0; i < N; i++) {
      current[i] = this._charToDigit(ringString[i]);
    }

    let step = 0;

    // Main evolution loop
    while (step < steps) {
      const evenTick = (step & 1) === 0; // true for Rule-A, false for Rule-B

      for (let i = 0; i < N; i++) {
        const leftIndex  = (i + N - 1) % N;
        const rightIndex = (i + 1) % N;

        const L = current[leftIndex];
        const C = current[i];
        const R = current[rightIndex];

        if (evenTick) {
          // Rule-A: (left + self + right) mod 10
          next[i] = (L + C + R) % 10;
        } else {
          // Rule-B: (left * right + self) mod 10
          next[i] = (L * R + C) % 10;
        }
      }

      // Swap buffers
      for (let i = 0; i < N; i++) {
        const tmp = current[i];
        current[i] = next[i];
        next[i] = tmp; // next becomes old value of current (not used further this tick)
      }

      step += 1;
    }

    // Build finalRing string, sumDigits, and digitCounts
    let finalRing = "";
    let sumDigits = 0;
    const digitCounts = Array(10).fill(0);

    for (let i = 0; i < N; i++) {
      const digit = current[i];
      finalRing += digit.toString();
      sumDigits += digit;
      digitCounts[digit] += 1;
    }

    return {
      finalRing,
      sumDigits,
      digitCounts,
      stepsExecuted: steps
    };
  }
}
