    let cur = this._stringToDigits(ringString);

    // Perform the simulation for the given number of steps
    for (let t = 0; t < steps; t++) {
      const next = new Array(N);
      for (let i = 0; i < N; i++) {
        const neighborIdx = (i + 1) % N;
        next[i] = (cur[i] + cur[neighborIdx]) % 10;
      }
      cur = next;
    }

    // Build counts array
    const counts = new Array(10).fill(0);
    for (let i = 0; i < N; i++) {
      counts[cur[i]]++;
    }

    // Build final ring string
    let finalRing = "";
    for (let i = 0; i < N; i++) {
      finalRing += cur[i];
    }

    // Compose result object
    const result = {
      ringLength: N,
      finalRing: finalRing,
      counts: counts
    };

