class Simulation {
  constructor() {}
  run(p) {
    const H = p.grid.length;
    const W = p.grid[0].length;
    // deep copy so caller object is never mutated
    const grid = p.grid.map(row => row.slice());
    let r = p.ant_row;
    let c = p.ant_col;
    let dir = p.ant_dir; // 0=up,1=right,2=down,3=left
    const dR = [-1, 0, 1, 0];
    const dC = [ 0, 1, 0,-1];
    const steps = p.steps;
    for (let s = 0; s < steps; s++) {
      const val = grid[r][c];
      // deterministic turn
      dir = (dir + (val % 2 === 0 ? 3 : 1)) & 3; // &3 == mod 4
      // rotate the rotor
      grid[r][c] = (val + 1) & 3;
      // move with toroidal wrapping
      r = (r + dR[dir] + H) % H;
      c = (c + dC[dir] + W) % W;
    }
    return { grid, ant: { row: r, col: c, dir }, step: steps };
  }
}

