class Simulation {
  constructor() {
    // no mutable state kept between runs
  }

  // helper: deep copy 2-D grid and validate contents
  _cloneAndValidateGrid(grid) {
    if (!Array.isArray(grid) || grid.length === 0)
      throw new Error("grid must be a non-empty 2-D array");

    const H = grid.length;
    const W = Array.isArray(grid[0]) ? grid[0].length : 0;
    if (W === 0) throw new Error("grid rows must be non-empty arrays");

    const cloned = new Array(H);

    for (let r = 0; r < H; r++) {
      const row = grid[r];
      if (!Array.isArray(row) || row.length !== W)
        throw new Error("grid must be rectangular");

      const newRow = new Array(W);
      for (let c = 0; c < W; c++) {
        const val = row[c];
        if (val !== 0 && val !== 1 && val !== 2 && val !== 3)
          throw new Error("orientation values must be integers 03");
        newRow[c] = val; // copy
      }
      cloned[r] = newRow;
    }
    return { cloned, H, W };
  }

  run(parameters) {
    // extract & validate parameters
    if (typeof parameters !== "object" || parameters === null)
      throw new Error("parameters must be an object");

    const { grid, antRow, antCol, antDir, steps } = parameters;

    const { cloned: g, H, W } = this._cloneAndValidateGrid(grid);

    const dirOK = antDir === 0 || antDir === 1 || antDir === 2 || antDir === 3;
    if (!dirOK) throw new Error("antDir must be 03");

    if (!Number.isInteger(steps) || steps < 0)
      throw new Error("steps must be a non-negative integer");

    // wrap initial ant position (allows out-of-range, yields same modulo result)
    let r = ((antRow % H) + H) % H;
    let c = ((antCol % W) + W) % W;
    let d = antDir | 0; // ensure int 03

    const dR = [-1, 0, 1, 0];
    const dC = [0, 1, 0, -1];

    for (let s = 0; s < steps; s++) {
      const val = g[r][c];

      // turn
      if ((val & 1) === 0) {
        d = (d + 3) & 3; // left
      } else {
        d = (d + 1) & 3; // right
      }

      // rotor update
      g[r][c] = (val + 1) & 3;

      // move with wrap
      r = (r + dR[d] + H) % H;
      c = (c + dC[d] + W) % W;
    }

    return {
      grid: g,
      ant: { row: r, col: c, dir: d },
      step: steps
    };
  }
}
