// VALIDATOR IMPLEMENTATION FOR: Mixed-Radix Counter Chain

class Simulation {
  constructor() {
    // No internal mutable state is kept between runs
  }

  // Helper to compute derived statistics
  _computeStats(counters) {
    const N = counters.length;
    if (N === 0) {
      return {
        sum: 0,
        zeroCount: 0,
        maxValue: 0,
        product: 1
      };
    }

    let sum = 0;
    let zeroCount = 0;
    let maxValue = counters[0];
    let product = 1;

    for (let i = 0; i < N; i++) {
      const val = counters[i];
      sum += val;
      if (val === 0) zeroCount += 1;
      if (val > maxValue) maxValue = val;
      product *= val;
    }

    return { sum, zeroCount, maxValue, product };
  }

  run(parameters) {
    // Extract parameters with defaults
    const moduli = Array.isArray(parameters?.moduli) ? parameters.moduli.slice() : [];
    const steps = Number.isFinite(parameters?.steps) && parameters.steps > 0 ? Math.floor(parameters.steps) : 0;

    const N = moduli.length;

    // Edge case: N === 0
    if (N === 0) {
      return { counters: [], ...this._computeStats([]) };
    }

    // Initialize counters to zeros
    const counters = new Array(N).fill(0);

    // Main simulation loop following the provided pseudocode exactly
    for (let step = 1; step <= steps; step++) {
      let carry = 1; // initial increment of counter[0]

      for (let i = 0; i < N; i++) {
        if (carry === 0) break;

        counters[i] += carry;

        const modulus = moduli[i];
        const newCarry = Math.floor(counters[i] / modulus);
        counters[i] = counters[i] % modulus;

        carry = newCarry;
      }
      // Any residual carry beyond the last counter is discarded
    }

    // Compute summary statistics
    const stats = this._computeStats(counters);

    // Assemble result matching the return schema
    return {
      counters,
      ...stats
    };
  }
}
