class Simulation {
  constructor() {}

  run(parameters) {
    const { N, modulus, coefficients, initial_values, steps } = parameters;
    const [a, b, c, d] = coefficients;

    // Normalize inputs to valid range [0, modulus-1]
    let values = initial_values.map(v => ((v % modulus) + modulus) % modulus);
    const norm_a = ((a % modulus) + modulus) % modulus;
    const norm_b = ((b % modulus) + modulus) % modulus;
    const norm_c = ((c % modulus) + modulus) % modulus;
    const norm_d = ((d % modulus) + modulus) % modulus;

    for (let step = 0; step < steps; step++) {
      const new_values = new Array(N);
      for (let i = 0; i < N; i++) {
        const left = values[(i - 1 + N) % N];
        const right = values[(i + 1) % N];
        const current = values[i];
        new_values[i] = (norm_a * current + norm_b * left + norm_c * right + norm_d) % modulus;
      }
      values = new_values;
    }

    const sum = values.reduce((acc, val) => acc + val, 0);
    const max_value = Math.max(...values);
    const max_position = values.indexOf(max_value);
    const zero_count = values.filter(v => v === 0).length;

    return {
      values: values,
      sum: sum,
      max_value: max_value,
      max_position: max_position,
      zero_count: zero_count,
      steps_executed: steps
    };
  }
}

