class Simulation {
  constructor() {
    // no internal mutable state required between runs
  }

  // Helper to normalize a number into [0, modulus-1]
  _norm(x, m) {
    return ((x % m) + m) % m;
  }

  run(parameters) {
    const modulus = parameters.modulus;
    const steps = parameters.steps;

    // Extract and normalize coefficients
    const coeffs = parameters.coefficients.slice(0, 4).map(c => this._norm(c, modulus));
    const [a, b, c, d] = coeffs;

    // Initialize and normalize values array
    const initialValues = parameters.initial_values.slice(); // copy to avoid mutating caller data
    const N = initialValues.length;
    let values = initialValues.map(v => this._norm(v, modulus));

    // Edge case: execute 0 steps -> return immediately after summarizing initial state
    const totalSteps = steps === 0 ? 0 : steps;

    for (let step = 0; step < totalSteps; step++) {
      const newValues = new Array(N);
      for (let i = 0; i < N; i++) {
        const leftIdx = (i - 1 + N) % N;
        const rightIdx = (i + 1) % N;

        const current = values[i];
        const left = values[leftIdx];
        const right = values[rightIdx];

        newValues[i] = this._norm(
          a * current + b * left + c * right + d,
          modulus
        );
      }
      values = newValues;
    }

    // Compute summary statistics
    let sum = 0;
    let max_value = -1;
    let max_position = 0;
    let zero_count = 0;

    for (let i = 0; i < N; i++) {
      const val = values[i];
      sum += val;

      if (val === 0) zero_count++;

      if (val > max_value) {
        max_value = val;
        max_position = i; // first occurrence kept due to ascending iteration
      }
    }

    return {
      values: values,
      sum: sum,
      max_value: max_value,
      max_position: max_position,
      zero_count: zero_count,
      steps_executed: totalSteps
    };
  }
}
