function simulate(initialNodes, initialConduits, initialSourcesSinks, maxTicks):
    nodes = deepCopy(initialNodes)
    conduits = deepCopy(initialConduits)
    sourcesSinks = deepCopy(initialSourcesSinks)
    tickCount = 0

    while tickCount < maxTicks:
        // 1. Calculate Node Pressures
        for each node in nodes:
            node.pressure = floor(node.essence / node.pressure_coefficient)

        // 2. Determine Conduit Flows
        provisionalFlows = [] // [conduit_id, amount, source_node_id, dest_node_id]
        committedFlowThisStep = {} // {node_id: {target_node_id: committed_amount}}

        // Sort conduits for deterministic processing
        sortedConduits = sort(conduits by id)

        for each conduit C in sortedConduits:
            if not C.is_active: continue

            fromNode = findNode(nodes, C.from_node_id)
            toNode = findNode(nodes, C.to_node_id)

            if not fromNode or not toNode: continue // Skip if nodes don't exist

            actualFlow = 0

            if fromNode.pressure > toNode.pressure:
                // Calculate available essence from 'fromNode' considering previous commitments to other conduits
                committedFromNode = sumCommitted(committedFlowThisStep, fromNode.id)
                availableEssenceFrom = fromNode.essence - committedFromNode

                actualFlow = min(C.flow_rate, availableEssenceFrom, C.capacity)
                if actualFlow > 0:
                    addCommitted(committedFlowThisStep, fromNode.id, toNode.id, actualFlow)
                    provisionalFlows.push([C.id, actualFlow, fromNode.id, toNode.id])
            else if toNode.pressure > fromNode.pressure:
                // Calculate available essence from 'toNode' considering previous commitments
                committedToNode = sumCommitted(committedFlowThisStep, toNode.id)
                availableEssenceTo = toNode.essence - committedToNode

                actualFlow = min(C.flow_rate, availableEssenceTo, C.capacity)
                if actualFlow > 0:
                    addCommitted(committedFlowThisStep, toNode.id, fromNode.id, actualFlow)
                    provisionalFlows.push([C.id, actualFlow, toNode.id, fromNode.id])

        // 3. Apply Conduit Flows
        for each flow in provisionalFlows:
            conduitId, amount, sourceId, destId = flow
            findNode(nodes, sourceId).essence -= amount
            findNode(nodes, destId).essence += amount

        // 4. Apply Source/Sink Effects
        for each ss in sourcesSinks:
            targetNode = findNode(nodes, ss.node_id)
            if not targetNode: continue

            if ss.type === 'source' and targetNode.pressure < ss.threshold_pressure:
                targetNode.essence += ss.rate
            else if ss.type === 'sink' and targetNode.pressure > ss.threshold_pressure:
                targetNode.essence = max(0, targetNode.essence - ss.rate)

        // 5. Check Conduit Failures
        for each conduit C in conduits:
            if C.is_active:
                fromNode = findNode(nodes, C.from_node_id)
                toNode = findNode(nodes, C.to_node_id)
                if (fromNode and fromNode.essence <= 0) or (toNode and toNode.essence <= 0):
                    C.is_active = false

        tickCount += 1

    return { nodes: nodes, conduits: conduits, sourcesSinks: sourcesSinks, tickCount: tickCount }

// Helper functions (example)
function findNode(nodesArray, nodeId) { return nodesArray.find(n => n.id === nodeId); }
function deepCopy(obj) { return JSON.parse(JSON.stringify(obj)); }
function sumCommitted(committedTracker, nodeId) {
    let sum = 0;
    if (committedTracker[nodeId]) {
        for (const target in committedTracker[nodeId]) {
            sum += committedTracker[nodeId][target];
        }
    }
    return sum;
}
function addCommitted(committedTracker, sourceId, targetId, amount) {
    if (!committedTracker[sourceId]) committedTracker[sourceId] = {};
    committedTracker[sourceId][targetId] = (committedTracker[sourceId][targetId] || 0) + amount;
}

