class Simulation {
  constructor() {
    // Internal state variables, no randomness
  }

  run(parameters) {
    let nodes = JSON.parse(JSON.stringify(parameters.initialNodes || []));
    let conduits = JSON.parse(JSON.stringify(parameters.initialConduits || []));
    let sourcesSinks = JSON.parse(JSON.stringify(parameters.initialSourcesSinks || []));
    let maxTicks = parameters.maxTicks || 0;
    let tickCount = 0;

    // Ensure nodes have 'essence' and 'pressure_coefficient'
    nodes = nodes.map(node => ({
        id: node.id,
        essence: node.essence !== undefined ? node.essence : 0,
        pressure_coefficient: node.pressure_coefficient > 0 ? node.pressure_coefficient : 1, // Ensure > 0
        pressure: 0 // Placeholder, calculated each tick
    }));

    // Ensure conduits have 'is_active'
    conduits = conduits.map(conduit => ({
        id: conduit.id,
        from_node_id: conduit.from_node_id,
        to_node_id: conduit.to_node_id,
        capacity: conduit.capacity > 0 ? conduit.capacity : 1,
        flow_rate: conduit.flow_rate > 0 ? conduit.flow_rate : 1,
        is_active: conduit.is_active !== undefined ? conduit.is_active : true
    }));

    // Helper function to find a node by ID
    const findNode = (nodeId) => nodes.find(n => n.id === nodeId);

    while (tickCount < maxTicks) {
      // 1. Calculate Node Pressures
      for (let i = 0; i < nodes.length; i++) {
        nodes[i].pressure = Math.floor(nodes[i].essence / nodes[i].pressure_coefficient);
      }

      // 2. Determine Conduit Flows
      const provisionalFlows = []; // [conduit_id, amount, source_node_id, dest_node_id]
      const committedFlowThisStep = {}; // {node_id: {target_node_id: committed_amount}}

      // Helper to sum committed flow for a node
      const sumCommitted = (nodeId) => {
        let sum = 0;
        if (committedFlowThisStep[nodeId]) {
          for (const targetId in committedFlowThisStep[nodeId]) {
            sum += committedFlowThisStep[nodeId][targetId];
          }
        }
        return sum;
      };

      // Helper to add committed flow
      const addCommitted = (sourceId, targetId, amount) => {
        if (!committedFlowThisStep[sourceId]) {
          committedFlowThisStep[sourceId] = {};
        }
        committedFlowThisStep[sourceId][targetId] = (committedFlowThisStep[sourceId][targetId] || 0) + amount;
      };

      // Sort conduits for deterministic processing
      const sortedConduits = [...conduits].sort((a, b) => a.id.localeCompare(b.id));

      for (let i = 0; i < sortedConduits.length; i++) {
        const C = sortedConduits[i];
        if (!C.is_active) {
          continue;
        }

        const fromNode = findNode(C.from_node_id);
        const toNode = findNode(C.to_node_id);

        if (!fromNode || !toNode) {
          continue; // Skip if nodes don't exist
        }

        let actualFlow = 0;

        if (fromNode.pressure > toNode.pressure) {
          const committedFromNode = sumCommitted(fromNode.id);
          const availableEssenceFrom = fromNode.essence - committedFromNode;

          actualFlow = Math.min(C.flow_rate, availableEssenceFrom, C.capacity);
          if (actualFlow > 0) {
            addCommitted(fromNode.id, toNode.id, actualFlow);
            provisionalFlows.push([C.id, actualFlow, fromNode.id, toNode.id]);
          }
        } else if (toNode.pressure > fromNode.pressure) {
          const committedToNode = sumCommitted(toNode.id);
          const availableEssenceTo = toNode.essence - committedToNode;

          actualFlow = Math.min(C.flow_rate, availableEssenceTo, C.capacity);
          if (actualFlow > 0) {
            addCommitted(toNode.id, fromNode.id, actualFlow);
            provisionalFlows.push([C.id, actualFlow, toNode.id, fromNode.id]);
          }
        }
      }

      // 3. Apply Conduit Flows
      for (let i = 0; i < provisionalFlows.length; i++) {
        const [, amount, sourceId, destId] = provisionalFlows[i];
        const sourceNode = findNode(sourceId);
        const destNode = findNode(destId);
        if (sourceNode) sourceNode.essence -= amount;
        if (destNode) destNode.essence += amount;
      }

      // 4. Apply Source/Sink Effects
      for (let i = 0; i < sourcesSinks.length; i++) {
        const ss = sourcesSinks[i];
        const targetNode = findNode(ss.node_id);
        if (!targetNode) {
          continue;
        }

        if (ss.type === 'source' && targetNode.pressure < ss.threshold_pressure) {
          targetNode.essence += ss.rate;
        } else if (ss.type === 'sink' && targetNode.pressure > ss.threshold_pressure) {
          targetNode.essence = Math.max(0, targetNode.essence - ss.rate);
        }
      }

      // 5. Check Conduit Failures
      for (let i = 0; i < conduits.length; i++) {
        const C = conduits[i];
        if (C.is_active) {
          const fromNode = findNode(C.from_node_id);
          const toNode = findNode(C.to_node_id);
          if ((fromNode && fromNode.essence <= 0) || (toNode && toNode.essence <= 0)) {
            C.is_active = false;
          }
        }
      }
      tickCount++;
    }

    // Re-calculate final pressures for return schema consistency
    for (let i = 0; i < nodes.length; i++) {
        nodes[i].pressure = Math.floor(nodes[i].essence / nodes[i].pressure_coefficient);
    }

    return { nodes, conduits, sourcesSinks, tickCount };
  }
}

