// VALIDATOR IMPLEMENTATION FOR: Elemental Conduit Flow

class Simulation {
  constructor() {
    // no global state
  }

  // ---------- Helper Utilities ----------
  _deepCopy(obj) {
    return JSON.parse(JSON.stringify(obj));
  }

  _findNode(nodesArr, nodeId) {
    for (let i = 0; i < nodesArr.length; i++) {
      if (nodesArr[i].id === nodeId) return nodesArr[i];
    }
    return null;
  }

  _sumCommitted(committedTracker, nodeId) {
    let sum = 0;
    if (committedTracker[nodeId]) {
      const targets = committedTracker[nodeId];
      for (const t in targets) {
        sum += targets[t];
      }
    }
    return sum;
  }

  _addCommitted(committedTracker, sourceId, targetId, amount) {
    if (!committedTracker[sourceId]) committedTracker[sourceId] = {};
    committedTracker[sourceId][targetId] =
      (committedTracker[sourceId][targetId] || 0) + amount;
  }

  // ---------- Main Simulation ----------
  run(parameters) {
    // Extract parameters with fallbacks
    const initialNodes = parameters.nodes || [];
    const initialConduits = parameters.conduits || [];
    const initialSourcesSinks = parameters.sourcesSinks || [];
    const maxTicks = parameters.maxTicks || 0;

    // Deep copies so we never mutate caller data
    const nodes = this._deepCopy(initialNodes);
    const conduits = this._deepCopy(initialConduits);
    const sourcesSinks = this._deepCopy(initialSourcesSinks);

    let tickCount = 0;

    while (tickCount < maxTicks) {
      // ----- 1. Calculate Node Pressures -----
      for (let i = 0; i < nodes.length; i++) {
        const n = nodes[i];
        if (n.essence < 0) n.essence = 0; // clamp if needed
        n.pressure = Math.floor(n.essence / n.pressure_coefficient);
      }

      // ----- 2. Determine Conduit Flows -----
      const provisionalFlows = []; // [conduit_id, amount, source_id, dest_id]
      const committedFlowThisStep = {}; // {node_id:{target_node_id: amount}}

      // Sort conduits lexicographically by id for deterministic order
      const sortedConduits = conduits
        .slice()
        .sort((a, b) => (a.id < b.id ? -1 : a.id > b.id ? 1 : 0));

      for (let i = 0; i < sortedConduits.length; i++) {
        const C = sortedConduits[i];
        if (!C.is_active) continue;

        const fromNode = this._findNode(nodes, C.from_node_id);
        const toNode = this._findNode(nodes, C.to_node_id);
        if (!fromNode || !toNode) continue; // ignore invalid endpoints

        let actualFlow = 0;

        if (fromNode.pressure > toNode.pressure) {
          const committedFrom = this._sumCommitted(
            committedFlowThisStep,
            fromNode.id
          );
          const availableEssence = fromNode.essence - committedFrom;
          if (availableEssence > 0) {
            actualFlow = Math.min(
              C.flow_rate,
              availableEssence,
              C.capacity
            );
          }
          if (actualFlow > 0) {
            this._addCommitted(
              committedFlowThisStep,
              fromNode.id,
              toNode.id,
              actualFlow
            );
            provisionalFlows.push([C.id, actualFlow, fromNode.id, toNode.id]);
          }
        } else if (toNode.pressure > fromNode.pressure) {
          const committedFrom = this._sumCommitted(
            committedFlowThisStep,
            toNode.id
          );
          const availableEssence = toNode.essence - committedFrom;
          if (availableEssence > 0) {
            actualFlow = Math.min(
              C.flow_rate,
              availableEssence,
              C.capacity
            );
          }
          if (actualFlow > 0) {
            this._addCommitted(
              committedFlowThisStep,
              toNode.id,
              fromNode.id,
              actualFlow
            );
            provisionalFlows.push([C.id, actualFlow, toNode.id, fromNode.id]);
          }
        }
        // equal pressure => no flow
      }

      // ----- 3. Apply Conduit Flows -----
      for (let i = 0; i < provisionalFlows.length; i++) {
        const [ , amount, sourceId, destId] = provisionalFlows[i];
        const srcNode = this._findNode(nodes, sourceId);
        const dstNode = this._findNode(nodes, destId);
        if (srcNode) srcNode.essence -= amount;
        if (dstNode) dstNode.essence += amount;
      }

      // ----- Recalculate Pressures After Flows -----
      for (let i = 0; i < nodes.length; i++) {
        const n = nodes[i];
        if (n.essence < 0) n.essence = 0;
        n.pressure = Math.floor(n.essence / n.pressure_coefficient);
      }

      // ----- 4. Apply Source/Sink Effects -----
      for (let i = 0; i < sourcesSinks.length; i++) {
        const S = sourcesSinks[i];
        const targetNode = this._findNode(nodes, S.node_id);
        if (!targetNode) continue;

        if (
          S.type === 'source' &&
          targetNode.pressure < S.threshold_pressure
        ) {
          targetNode.essence += S.rate;
        } else if (
          S.type === 'sink' &&
          targetNode.pressure > S.threshold_pressure
        ) {
          targetNode.essence = Math.max(0, targetNode.essence - S.rate);
        }
      }

      // (pressure not recomputed after source/sink per specification example)

      // ----- 5. Check Conduit Failures -----
      for (let i = 0; i < conduits.length; i++) {
        const C = conduits[i];
        if (!C.is_active) continue;

        const fromNode = this._findNode(nodes, C.from_node_id);
        const toNode = this._findNode(nodes, C.to_node_id);

        const fromEssenceNonPositive =
          fromNode ? fromNode.essence <= 0 : false;
        const toEssenceNonPositive = toNode ? toNode.essence <= 0 : false;

        if (fromEssenceNonPositive || toEssenceNonPositive) {
          C.is_active = false;
        }
      }

      // ----- 6. Increment tick counter -----
      tickCount += 1;
    }

    // ---------- Final Return ----------
    return {
      nodes: nodes,
      conduits: conduits,
      sourcesSinks: sourcesSinks,
      tickCount: tickCount
    };
  }
}
