class Simulation {
  constructor() {}

  run(parameters) {
    const { height, width, modulus, initial_pattern, max_steps } = parameters;
    const H = height;
    const W = width;
    const M = modulus;

    if (H === 0 || W === 0) {
      return { grid: [], step_count: 0, sum_amplitudes: 0 };
    }

    // Initialize grid
    let grid = [];
    for (let r = 0; r < H; r++) {
      grid[r] = [];
      for (let c = 0; c < W; c++) {
        const idx = r * W + c;
        const char = initial_pattern[idx % initial_pattern.length];
        grid[r][c] = char.charCodeAt(0) % M;
      }
    }

    // Simulate steps
    for (let step = 0; step < max_steps; step++) {
      const new_grid = [];
      for (let r = 0; r < H; r++) {
        new_grid[r] = [];
        for (let c = 0; c < W; c++) {
          const north = grid[(r - 1 + H) % H][c];
          const south = grid[(r + 1) % H][c];
          const east = grid[r][(c + 1) % W];
          const west = grid[r][(c - 1 + W) % W];
          new_grid[r][c] = (north + south + east + west) % M;
        }
      }
      grid = new_grid;
    }

    // Calculate sum
    let sum = 0;
    for (let r = 0; r < H; r++) {
      for (let c = 0; c < W; c++) {
        sum += grid[r][c];
      }
    }

    return {
      grid: grid,
      step_count: max_steps,
      sum_amplitudes: sum
    };
  }
}

