class Simulation {
  constructor() {
    // No global state  everything is rebuilt per run
  }

  // Helper to build a 2-D array
  _make2D(rows, cols) {
    const arr = new Array(rows);
    for (let r = 0; r < rows; r++) {
      arr[r] = new Array(cols);
    }
    return arr;
  }

  run(parameters) {
    // Extract parameters with default fallbacks
    const H          = parameters.H          >>> 0;       // ensure non-negative int
    const W          = parameters.W          >>> 0;
    const M          = parameters.M          >>> 0 || 1;  // modulus must be 1
    const patternStr = String(parameters.initial_pattern || "");
    const maxSteps   = parameters.max_steps  >>> 0;

    // Edge case: empty grid
    if (H === 0 || W === 0) {
      return { grid: [], step_count: 0, sum_amplitudes: 0 };
    }

    // Initialise grid
    let grid = this._make2D(H, W);
    const patternLen = patternStr.length;

    for (let idx = 0; idx < H * W; idx++) {
      let code = 0;
      if (patternLen) {
        const ch = patternStr[idx % patternLen];
        code = ch.charCodeAt(0) & 0xffff; // ensure integer 065535
      }
      grid[Math.floor(idx / W)][idx % W] = code % M;
    }

    // Simulation loop
    for (let step = 0; step < maxSteps; step++) {
      const newGrid = this._make2D(H, W);

      for (let r = 0; r < H; r++) {
        const rNorth = (r - 1 + H) % H;
        const rSouth = (r + 1) % H;

        for (let c = 0; c < W; c++) {
          const cWest = (c - 1 + W) % W;
          const cEast = (c + 1) % W;

          const north = grid[rNorth][c];
          const south = grid[rSouth][c];
          const east  = grid[r][cEast];
          const west  = grid[r][cWest];

          newGrid[r][c] = (north + south + east + west) % M;
        }
      }
      grid = newGrid;
    }

    // Sum amplitudes
    let sum = 0;
    for (let r = 0; r < H; r++) {
      for (let c = 0; c < W; c++) {
        sum += grid[r][c];
      }
    }

    return {
      grid: grid,
      step_count: maxSteps,
      sum_amplitudes: sum
    };
  }
}
