// VALIDATOR IMPLEMENTATION FOR: Modular Wave Automaton

class Simulation {
  constructor() {
    // No global mutable state required across runs
    this.MODULUS = 7; // constant M
  }

  // Helper to validate and convert a single character digit to integer 0-6
  _parseDigit(ch) {
    const code = ch.charCodeAt(0);
    // '0' -> 48, '6' -> 54
    if (code < 48 || code > 54) {
      throw new Error("Initial string contains invalid digit (allowed 0-6)");
    }
    return code - 48; // numeric value
  }

  run(parameters) {
    // Extract parameters
    if (typeof parameters !== "object" || parameters === null) {
      throw new Error("Parameters must be an object");
    }
    const initial = parameters.initial;
    const stepsInput = parameters.steps;

    if (typeof initial !== "string") {
      throw new Error("Parameter 'initial' must be a string");
    }
    if (initial.length === 0) {
      throw new Error("Initial string must not be empty");
    }

    // steps must be an integer, >=0
    const steps = Number(stepsInput);
    if (!Number.isInteger(steps) || steps < 0) {
      throw new Error("Parameter 'steps' must be a non-negative integer");
    }

    // Initialization
    const N = initial.length;
    const state = new Array(N);
    for (let i = 0; i < N; i++) {
      state[i] = this._parseDigit(initial[i]);
    }

    // Precompute wave coefficients w[i] = ((i+1)*(i+2)) mod 7
    const w = new Array(N);
    for (let i = 0; i < N; i++) {
      const val = (i + 1) * (i + 2);
      w[i] = val % this.MODULUS; // guaranteed non-negative
    }

    // Simulation loop
    let current = state;
    for (let step = 0; step < steps; step++) {
      const newState = new Array(N);
      for (let i = 0; i < N; i++) {
        const left = current[(i - 1 + N) % N];
        const self = current[i];
        const right = current[(i + 1) % N];
        const updated =
          (left + w[i] * self + right) % this.MODULUS;
        newState[i] = updated;
      }
      current = newState;
    }

    // Derive properties
    const final_state = current;
    let sum = 0;
    let count_zeros = 0;
    let max_value = 0;
    let pattern_hash = 0;

    for (let i = 0; i < N; i++) {
      const val = final_state[i];
      sum += val;
      if (val === 0) count_zeros++;
      if (val > max_value) max_value = val;
      pattern_hash += i * val;
    }

    // Assemble result
    const result = {
      final_state,
      sum,
      count_zeros,
      max_value,
      pattern_hash
    };
    return result;
  }
}
