  _mod(a, m) {
    const res = a % m;
    return res < 0 ? res + m : res;
  }

  run(parameters) {
    // Extract parameters
    const N = parameters.N;
    const M = parameters.M;
    const steps = parameters.steps;
    const generators = parameters.generators; // array of {position, amplitude, speed}

