        const gen = generators[g];
        const pos = gen.position;
        const amp = gen.amplitude;
        const speed = gen.speed;

        const leftPos = this._mod(pos - speed * t, N);
        const rightPos = this._mod(pos + speed * t, N);

        // Add amplitude to left position (mod M)
        temp[leftPos] = this._mod(temp[leftPos] + amp, M);

        // Add amplitude to right position (mod M)
        temp[rightPos] = this._mod(temp[rightPos] + amp, M);
