    let currentZeroRun = 0;
    let checksum = 0;

    for (let i = 0; i < N; i++) {
      sum += cells[i];
      checksum = (checksum + i * cells[i]) % 1000007;

      if (cells[i] === 0) {
        zeros++;
        currentZeroRun++;
        maxConsecutiveZeros = Math.max(maxConsecutiveZeros, currentZeroRun);
      } else {
        currentZeroRun = 0;
      }
    }

    // Check wraparound for consecutive zeros
    if (cells[0] === 0 && cells[N-1] === 0) {
      let startRun = 0;
      for (let i = 0; i < N && cells[i] === 0; i++) {
        startRun++;
      }
      let endRun = 0;
      for (let i = N-1; i >= 0 && cells[i] === 0; i--) {
        endRun++;
      }
      if (startRun + endRun === N) {
        maxConsecutiveZeros = N;
      } else if (startRun < N && endRun < N) {
        maxConsecutiveZeros = Math.max(maxConsecutiveZeros, startRun + endRun);
      }
    }

    return {
      cells: cells,
      sum: sum,
      zeros: zeros,
      max_consecutive_zeros: maxConsecutiveZeros,
      checksum: checksum
    };
  }
}

