// VALIDATOR IMPLEMENTATION FOR: Fibonacci Modular Ring

class Simulation {
  constructor() {
    // No persistent state required between runs
  }

  // Helper: non-negative modulo
  _mod(a, m) {
    const res = a % m;
    return res < 0 ? res + m : res;
  }

  run(parameters) {
    // ----- Parameter extraction -----
    const initialStr = String(parameters.initial || "");
    const P = Number(parameters.prime);
    const steps = Number(parameters.steps);

    const N = initialStr.length;
    const cells = new Array(N);

    // ----- Initialisation -----
    for (let i = 0; i < N; i++) {
      // Each character is expected to be '0' or '1'
      cells[i] = initialStr.charCodeAt(i) - 48; // faster than parseInt
    }

    // ----- Simulation loop -----
    for (let s = 0; s < steps; s++) {
      const newCells = new Array(N);
      for (let i = 0; i < N; i++) {
        const left = (i === 0 ? N - 1 : i - 1);
        newCells[i] = this._mod(cells[left] + cells[i], P);
      }
      // Replace old state
      for (let i = 0; i < N; i++) cells[i] = newCells[i];
    }

    // ----- Statistics -----
    let sum = 0;
    let zeros = 0;
    let maxConsecutiveZeros = 0;
    let currentRun = 0;
    let checksum = 0;
    const MOD_CHECK = 1000007;

    for (let i = 0; i < N; i++) {
      const val = cells[i];
      sum += val;

      if (val === 0) {
        zeros++;
        currentRun++;
        if (currentRun > maxConsecutiveZeros) maxConsecutiveZeros = currentRun;
      } else {
        currentRun = 0;
      }

      checksum = (checksum + (i * val)) % MOD_CHECK;
    }

    return {
      cells,
      sum,
      zeros,
      max_consecutive_zeros: maxConsecutiveZeros,
      checksum
    };
  }
}
