class Simulation {
  constructor() {}

  /**
   * Run the Parity-Flip Rotor Ring simulation.
   * @param {{cells:number[], steps:number, headIndex?:number, headDir?:number}} parameters
   * @returns {{cells:number[], headIndex:number, headDir:number}}
   */
  run(parameters) {
