    const cells = parameters.cells.slice();
    const N = cells.length;
    if (N === 0) throw new Error("Ring must contain at least one cell");

    let head = parameters.headIndex == null ? 0 : parameters.headIndex;
    head = ((head % N) + N) % N; // wrap to [0,N)

    let dir = parameters.headDir == null ? 1 : (parameters.headDir >= 0 ? 1 : -1);
    let steps = parameters.steps >>> 0; // force unsigned 32-bit

    for (let t = 0; t < steps; t++) {
      const v = cells[head];
      if (v & 1) dir = -dir;           // flip on odd
      cells[head] = (v + 1) & 0xFF;    // +1 mod 256
      head = (head + dir + N) % N;     // advance with wrap
    }

    return { cells, headIndex: head, headDir: dir };
  }
}

