// VALIDATOR IMPLEMENTATION FOR: Parity-Flip Rotor Ring

class Simulation {
  constructor() {
    // No global state; everything happens inside run()
  }

  // Helper for positive modulo
  _mod(x, m) {
    return ((x % m) + m) % m;
  }

  run(parameters) {
    // --- Initialisation ---
    const inputCells = Array.isArray(parameters.cells) ? parameters.cells : [];
    const N = inputCells.length;                   // MUST be  1 by spec
    if (N === 0) {
      throw new Error("cells array must contain at least one element");
    }

    // Deep copy cells and normalise to 0255
    const cells = inputCells.map(v => (v & 0xFF));

    // head.index
    const rawIndex = (parameters.hasOwnProperty('headIndex') ? parameters.headIndex : 0);
    let headIndex = this._mod(rawIndex, N);

    // head.dir
    const rawDir = (parameters.hasOwnProperty('headDir') ? parameters.headDir : 1);
    let headDir = (rawDir < 0 ? -1 : 1);          // 0 counts as +1

    // steps
    let steps = parameters.steps;
    if (typeof steps !== 'number' || !Number.isInteger(steps) || steps < 0 || steps > 1_000_000) {
      throw new Error("steps must be an integer 01_000_000");
    }

    // --- Simulation loop ---
    for (let t = 0; t < steps; t++) {
      const v = cells[headIndex];                 // value before increment

      if (v & 1) {                                // odd  flip direction
        headDir = -headDir;
      }

      cells[headIndex] = (v + 1) & 0xFF;          // increment mod 256

      headIndex = this._mod(headIndex + headDir, N); // advance with wrapping
    }

    // --- Return final snapshot ---
    return {
      cells: cells,
      headIndex: headIndex,
      headDir: headDir
    };
  }
}
