// VALIDATOR IMPLEMENTATION FOR: Cipher Weave

class Simulation {
  constructor() {
    // No persistent state needed
  }

  run(parameters) {
    // Destructure parameters for easy access
    const {
      tape_length,
      modulus,
      init_alpha_pos,
      init_alpha_state,
      init_beta_pos,
      init_beta_state,
      max_steps
    } = parameters;

    // ------------- Initialisation -------------
    const tape = new Array(tape_length);
    for (let i = 0; i < tape_length; i++) {
      tape[i] = i % modulus;
    }

    const alpha = { pos: init_alpha_pos, state: init_alpha_state };
    const beta  = { pos: init_beta_pos,  state: init_beta_state  };

    // ------------- Main Loop -------------
    for (let step = 0; step < max_steps; step++) {
      // Cache current step values
      const alpha_pos_t   = alpha.pos;
      const beta_pos_t    = beta.pos;
      const alpha_state_t = alpha.state;

      const val_at_alpha = tape[alpha_pos_t];
      const val_at_beta  = tape[beta_pos_t];

      // 1 & 2. Read and State Update
      const new_alpha_state = (alpha_state_t + val_at_beta) % modulus;
      const new_beta_state  = (beta.state    + val_at_alpha) % modulus;

      // 3. Tape Write (Alpha first, Beta second)
      tape[alpha_pos_t] = (val_at_alpha + new_beta_state) % modulus;
      tape[beta_pos_t]  = (val_at_beta  + new_alpha_state) % modulus;

      // 4. Move
      const delta_alpha = new_alpha_state + 1;
      const delta_beta  = new_beta_state  + 1;

      alpha.pos = (alpha_pos_t + delta_alpha) % tape_length;

      // Ensure non-negative modulo for Beta movement
      beta.pos = ((beta_pos_t - delta_beta) % tape_length + tape_length) % tape_length;

      // 5. Commit new states
      alpha.state = new_alpha_state;
      beta.state  = new_beta_state;
    }

    // ------------- Return Final State -------------
    return {
      step: max_steps,
      weaver_alpha: { pos: alpha.pos, state: alpha.state },
      weaver_beta:  { pos: beta.pos,  state: beta.state  },
      tape: tape
    };
  }
}
