class Simulation {
  constructor() {}
  // helper: XOR neighbours at distance 'd'
  _applyShift(state, d) {
    const n = state.length;
    const res = new Array(n);
    const shift = ((d % n) + n) % n;
    for (let i = 0; i < n; i++) {
      const left  = state[(i - shift + n) % n];
      const right = state[(i + shift) % n];
      res[i] = left ^ right;
    }
    return res;
  }
  run(parameters) {
    const ringStr = parameters.ring;
    const stepsIn = parameters.steps >>> 0; // enforce unsigned 32-bit semantics
    const n = ringStr.length;
    const current0 = new Array(n);
    for (let i = 0; i < n; i++) current0[i] = ringStr.charCodeAt(i) & 1; // '0'0, '1'1
    let current = current0;
    let steps = stepsIn;
    let shift = 1;
    while (steps !== 0) {
      if (steps & 1) current = this._applyShift(current, shift);
      steps >>>= 1;           // unsigned right shift keeps determinism
      if (steps === 0) break; // avoid pointless last double
      shift <<= 1;
    }
    let sum = 0;
    for (let i = 0; i < current.length; i++) sum += current[i];
    return sum;
  }
}

