// VALIDATOR IMPLEMENTATION FOR: Binary-Ring Rule-90

class Simulation {
  constructor() {
    // purely functional; no mutable shared state
  }

  // Helper: apply one Rule-90 bulk shift with distance s (mod N)
  _applyShift(arr, s) {
    const N = arr.length;
    const d = ((s % N) + N) % N;          // ensure 0  d < N
    const res = new Array(N);

    for (let i = 0; i < N; i++) {
      const leftIdx  = (i - d + N) % N;   // neighbour to the left
      const rightIdx = (i + d) % N;       // neighbour to the right
      res[i] = arr[leftIdx] ^ arr[rightIdx];
    }
    return res;
  }

  // Popcount  sum of 1-bits in the array
  _popcount(arr) {
    let sum = 0;
    for (let i = 0; i < arr.length; i++) sum += arr[i];
    return sum;
  }

  run(parameters) {
    // 1. parameter extraction & initialisation
    const ring  = String(parameters.ring);
    let steps   = Number(parameters.steps);

    const N = ring.length;
    const currentInit = new Array(N);
    for (let i = 0; i < N; i++) {
      currentInit[i] = ring.charAt(i) === '1' ? 1 : 0;
    }

    // Early exit if no evolution requested
    if (steps === 0) {
      return this._popcount(currentInit);
    }

    // 2. Main logarithmic evolution loop (per specification pseudocode)
    let current = currentInit;
    let shift   = 1;

    while (steps > 0) {
      if ((steps & 1) === 1) {
        current = this._applyShift(current, shift);
      }

      steps = steps >>> 1;          // unsigned right shift
      if (steps === 0) break;

      shift = shift << 1;           // doubling distance
    }

    // 3. Return Hamming weight of final configuration
    return this._popcount(current);
  }
}
